'use client';

import React, { createContext, useContext, useState } from 'react';

interface ToolbarConfig {
  title: string;
  showBackButton?: boolean;
  backHref?: string;
  actions?: React.ReactNode;
}

interface ToolbarContextType {
  config: ToolbarConfig;
  setToolbar: (config: ToolbarConfig) => void;
  isHidden: boolean;
  toggleHidden: () => void;
}

const defaultConfig: ToolbarConfig = {
  title: '',
};

const ToolbarContext = createContext<ToolbarContextType>({
  config: defaultConfig,
  setToolbar: () => {},
  isHidden: false,
  toggleHidden: () => {},
});

export function useToolbar() {
  return useContext(ToolbarContext);
}

export function ToolbarProvider({ children }: { children: React.ReactNode }) {
  const [config, setConfig] = useState<ToolbarConfig>(defaultConfig);
  const [isHidden, setIsHidden] = useState(false);

  const setToolbar = (config: ToolbarConfig) => {
    setConfig(config);
  };

  const toggleHidden = () => {
    setIsHidden(prev => !prev);
  };

  return (
    <ToolbarContext.Provider value={{ config, setToolbar, isHidden, toggleHidden }}>
      {children}
    </ToolbarContext.Provider>
  );
}

