'use client';

import { useState, useEffect } from 'react';
import { ShoppingCart, Receipt, TrendingUp, DollarSign } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { SalesCart } from '@/components/sales/SalesCart';
import { ProductSearch } from '@/components/sales/ProductSearch';
import { CustomerSelect } from '@/components/sales/CustomerSelect';
import { PaymentModal } from '@/components/sales/PaymentModal';
import { useToolbar } from '@/components/layout/ToolbarContext';
import { useCreateSale, useSalesStats } from '@/hooks/useSales';
import type { Product } from '@/types/product';
import type { CartItem, Cart } from '@/types/sale';

export default function SalesPage() {
  const [cart, setCart] = useState<Cart>({
    items: [],
    subtotal: 0,
    tax: 0,
    discount: 0,
    total: 0,
  });
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const { setToolbar } = useToolbar();

  useEffect(() => {
    setToolbar({ title: 'Ventas' });
  }, [setToolbar]);

  const { data: stats } = useSalesStats();
  const createSaleMutation = useCreateSale();

  const TAX_RATE = 0.19; // 19% IVA

  const calculateTotals = (items: CartItem[]) => {
    const subtotal = items.reduce((sum, item) => sum + item.subtotal, 0);
    const tax = subtotal * TAX_RATE;
    const total = subtotal + tax - cart.discount;

    return { subtotal, tax, total };
  };

  const handleAddProduct = (product: Product) => {
    if (product.stock_quantity === 0) {
      alert('Producto sin stock');
      return;
    }

    const existingItem = cart.items.find(
      (item) => item.product_id === product.id
    );

    let newItems: CartItem[];

    if (existingItem) {
      if (existingItem.quantity >= product.stock_quantity) {
        alert('No hay suficiente stock');
        return;
      }
      newItems = cart.items.map((item) =>
        item.product_id === product.id
          ? {
              ...item,
              quantity: item.quantity + 1,
              subtotal: (item.quantity + 1) * item.unit_price,
            }
          : item
      );
    } else {
      const newItem: CartItem = {
        product_id: product.id,
        product_name: product.name,
        quantity: 1,
        unit_price: product.price,
        tax_rate: product.tax_rate || TAX_RATE * 100,
        subtotal: product.price,
        stock_quantity: product.stock_quantity,
        image_url: product.image_url,
      };
      newItems = [...cart.items, newItem];
    }

    const totals = calculateTotals(newItems);
    setCart({ ...cart, items: newItems, ...totals });
  };

  const handleUpdateQuantity = (productId: string, quantity: number) => {
    if (quantity < 1) return;

    const newItems = cart.items.map((item) =>
      item.product_id === productId
        ? {
            ...item,
            quantity,
            subtotal: quantity * item.unit_price,
          }
        : item
    );

    const totals = calculateTotals(newItems);
    setCart({ ...cart, items: newItems, ...totals });
  };

  const handleRemoveItem = (productId: string) => {
    const newItems = cart.items.filter((item) => item.product_id !== productId);
    const totals = calculateTotals(newItems);
    setCart({ ...cart, items: newItems, ...totals });
  };

  const handleSelectCustomer = (
    customer: { id: string; name: string } | undefined
  ) => {
    setCart({
      ...cart,
      customer_id: customer?.id,
      customer_name: customer?.name,
    });
  };

  const handleCheckout = () => {
    if (cart.items.length === 0) {
      alert('El carrito está vacío');
      return;
    }
    setShowPaymentModal(true);
  };

  const handleConfirmPayment = async (
    paymentMethod: string,
    notes?: string
  ) => {
    try {
      const saleData = {
        customer_id: cart.customer_id,
        items: cart.items.map((item) => ({
          product_id: item.product_id,
          quantity: item.quantity,
          unit_price: item.unit_price,
          discount: item.discount || 0,
          tax_rate: item.tax_rate || TAX_RATE * 100,
        })),
        payment_method: paymentMethod,
        payment_status: 'paid' as const,
        discount_amount: cart.discount,
        notes,
      };

      await createSaleMutation.mutateAsync(saleData);

      alert('Venta completada exitosamente');
      setShowPaymentModal(false);
      // Reset cart
      setCart({
        items: [],
        subtotal: 0,
        tax: 0,
        discount: 0,
        total: 0,
      });
    } catch (error) {
      alert('Error al procesar la venta');
      console.error(error);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Punto de Venta</h1>
        <p className="text-gray-500 mt-1">Gestiona tus ventas y checkout</p>
      </div>

      {/* Stats Cards */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Ventas Hoy</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.todaySales}
                </p>
              </div>
              <ShoppingCart className="w-10 h-10 text-blue-500" />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Ingresos Hoy</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {formatCurrency(stats.todayRevenue)}
                </p>
              </div>
              <DollarSign className="w-10 h-10 text-green-500" />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Total Ventas</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalSales}
                </p>
              </div>
              <Receipt className="w-10 h-10 text-purple-500" />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Ticket Promedio</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {formatCurrency(stats.averageOrderValue)}
                </p>
              </div>
              <TrendingUp className="w-10 h-10 text-orange-500" />
            </div>
          </Card>
        </div>
      )}

      {/* Main Content */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Left side - Product search and customer */}
        <div className="lg:col-span-2 space-y-6">
          {/* Product search */}
          <Card className="p-6">
            <h2 className="text-lg font-semibold mb-4">Buscar Productos</h2>
            <ProductSearch onAddProduct={handleAddProduct} />
          </Card>

          {/* Customer selection */}
          <CustomerSelect
            selectedCustomer={
              cart.customer_id && cart.customer_name
                ? { id: cart.customer_id, name: cart.customer_name }
                : undefined
            }
            onSelect={handleSelectCustomer}
          />
        </div>

        {/* Right side - Cart */}
        <div className="space-y-4">
          <div className="lg:sticky lg:top-6">
            <h2 className="text-lg font-semibold mb-4 flex items-center gap-2">
              <ShoppingCart className="w-5 h-5" />
              Carrito ({cart.items.length})
            </h2>

            <div className="space-y-4">
              <SalesCart
                items={cart.items}
                onUpdateQuantity={handleUpdateQuantity}
                onRemoveItem={handleRemoveItem}
                subtotal={cart.subtotal}
                tax={cart.tax}
                discount={cart.discount}
                total={cart.total}
              />

              <Button
                onClick={handleCheckout}
                disabled={cart.items.length === 0}
                className="w-full"
                size="lg"
              >
                Procesar Venta
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Payment Modal */}
      <PaymentModal
        isOpen={showPaymentModal}
        onClose={() => setShowPaymentModal(false)}
        total={cart.total}
        onConfirm={handleConfirmPayment}
        isLoading={createSaleMutation.isPending}
      />
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Plus, Package, AlertTriangle, TrendingUp, DollarSign } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { ProductsTable } from '@/components/products/ProductsTable';
import { ProductFilters } from '@/components/products/ProductFilters';
import { ProductForm } from '@/components/products/ProductForm';
import { useToolbar } from '@/components/layout/ToolbarContext';
import {
  useProducts,
  useProductStats,
  useCreateProduct,
  useUpdateProduct,
  useDeleteProduct,
} from '@/hooks/useProducts';
import type { Product, ProductQueryParams, CreateProductDto } from '@/types/product';

export default function ProductsPage() {
  const { setToolbar } = useToolbar();
  const [queryParams, setQueryParams] = useState<ProductQueryParams>({
    page: 1,
    pageSize: 20,
  });
  const [showForm, setShowForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);

  // Queries
  const { data: productsData, isLoading } = useProducts(queryParams);
  const { data: stats } = useProductStats();

  // Mutations
  const createMutation = useCreateProduct();
  const updateMutation = useUpdateProduct();
  const deleteMutation = useDeleteProduct();

  useEffect(() => {
    setToolbar({ title: 'Productos' });
  }, [setToolbar]);

  const handleFilterChange = (filters: ProductQueryParams) => {
    setQueryParams({ ...filters, page: 1, pageSize: 20 });
  };

  const handleCreateNew = () => {
    setEditingProduct(null);
    setShowForm(true);
  };

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    setShowForm(true);
  };

  const handleView = (product: Product) => {
    // TODO: Implement product detail modal
    console.log('View product:', product);
  };

  const handleDelete = async (product: Product) => {
    if (
      window.confirm(
        `¿Estás seguro de eliminar el producto "${product.name}"? Esta acción es irreversible.`
      )
    ) {
      try {
        await deleteMutation.mutateAsync(product.id);
        alert('Producto eliminado exitosamente');
      } catch (error) {
        alert('Error al eliminar el producto');
        console.error(error);
      }
    }
  };

  const handleSubmit = async (data: CreateProductDto) => {
    try {
      if (editingProduct) {
        await updateMutation.mutateAsync({ id: editingProduct.id, data });
        alert('Producto actualizado exitosamente');
      } else {
        await createMutation.mutateAsync(data);
        alert('Producto creado exitosamente');
      }
      setShowForm(false);
      setEditingProduct(null);
    } catch (error) {
      alert(editingProduct ? 'Error al actualizar el producto' : 'Error al crear el producto');
      console.error(error);
    }
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingProduct(null);
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Productos</h1>
          <p className="text-gray-500 mt-1">Gestiona tu catálogo de productos</p>
        </div>
        {!showForm && (
          <Button onClick={handleCreateNew}>
            <Plus className="w-5 h-5 mr-2" />
            Nuevo Producto
          </Button>
        )}
      </div>

      {/* Stats Cards */}
      {stats && !showForm && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Total Productos</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalProducts}
                </p>
              </div>
              <Package className="w-10 h-10 text-blue-500" />
            </div>
            <div className="mt-4">
              <Badge variant="success">{stats.activeProducts} activos</Badge>
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Valor Total</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {formatCurrency(stats.totalValue)}
                </p>
              </div>
              <DollarSign className="w-10 h-10 text-green-500" />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Stock Bajo</p>
                <p className="text-2xl font-bold text-yellow-600 mt-1">
                  {stats.lowStockCount}
                </p>
              </div>
              <AlertTriangle className="w-10 h-10 text-yellow-500" />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Sin Stock</p>
                <p className="text-2xl font-bold text-red-600 mt-1">
                  {stats.outOfStockCount}
                </p>
              </div>
              <TrendingUp className="w-10 h-10 text-red-500" />
            </div>
          </Card>
        </div>
      )}

      {/* Form or Table */}
      {showForm ? (
        <Card className="p-6">
          <h2 className="text-xl font-semibold mb-6">
            {editingProduct ? 'Editar Producto' : 'Crear Nuevo Producto'}
          </h2>
          <ProductForm
            product={editingProduct || undefined}
            onSubmit={handleSubmit}
            onCancel={handleCancel}
            isLoading={createMutation.isPending || updateMutation.isPending}
          />
        </Card>
      ) : (
        <>
          {/* Filters */}
          <ProductFilters
            onFilterChange={handleFilterChange}
            categories={stats?.categories || []}
          />

          {/* Products Table */}
          <Card className="overflow-hidden">
            <ProductsTable
              products={productsData?.data || []}
              onEdit={handleEdit}
              onDelete={handleDelete}
              onView={handleView}
              isLoading={isLoading}
            />

            {/* Pagination */}
            {productsData && productsData.total > 0 && (
              <div className="px-6 py-4 border-t border-gray-200 flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  Mostrando {((productsData.page - 1) * productsData.pageSize) + 1} -{' '}
                  {Math.min(
                    productsData.page * productsData.pageSize,
                    productsData.total
                  )}{' '}
                  de {productsData.total} productos
                </div>
                <div className="flex gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    disabled={productsData.page === 1}
                    onClick={() =>
                      setQueryParams((prev) => ({ ...prev, page: prev.page! - 1 }))
                    }
                  >
                    Anterior
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    disabled={
                      productsData.page >=
                      Math.ceil(productsData.total / productsData.pageSize)
                    }
                    onClick={() =>
                      setQueryParams((prev) => ({ ...prev, page: prev.page! + 1 }))
                    }
                  >
                    Siguiente
                  </Button>
                </div>
              </div>
            )}
          </Card>
        </>
      )}
    </div>
  );
}

