    setEditingCustomer(null);
  };

  const handleLoyaltyConfirm = async (
    operation: 'add' | 'subtract' | 'set',
    points: number
  ) => {
    if (!loyaltyModalCustomer) return;

    try {
      await updateLoyaltyMutation.mutateAsync({
        id: loyaltyModalCustomer.id,
        data: { operation, points },
      });
      alert('Puntos actualizados exitosamente');
      setLoyaltyModalCustomer(null);
    } catch (error) {
      alert('Error al actualizar los puntos');
      console.error(error);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Clientes</h1>
          <p className="text-gray-500 mt-1">Gestiona tu base de clientes y puntos de lealtad</p>
        </div>
        {!showForm && (
          <Button onClick={handleCreateNew}>
            <Plus className="w-5 h-5 mr-2" />
            Nuevo Cliente
          </Button>
        )}
      </div>

      {/* Stats Cards */}
      {stats && !showForm && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Total Clientes</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalCustomers}
                </p>
              </div>
              <Users className="w-10 h-10 text-blue-500" />
            </div>
            <div className="mt-4 flex items-center gap-2">
              <span className="text-sm text-green-600 font-medium">
                {stats.activeCustomers} activos
              </span>
              <span className="text-sm text-gray-400">•</span>
              <span className="text-sm text-gray-500">
                {stats.inactiveCustomers} inactivos
              </span>
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Puntos Totales</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalLoyaltyPoints.toLocaleString()}
                </p>
              </div>
              <Award className="w-10 h-10 text-yellow-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Puntos de lealtad acumulados
            </p>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Compra Promedio</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {formatCurrency(stats.avgPurchaseValue)}
                </p>
              </div>
              <DollarSign className="w-10 h-10 text-green-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Valor promedio por cliente
            </p>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Clientes Recientes</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.recentCustomers}
                </p>
              </div>
              <TrendingUp className="w-10 h-10 text-purple-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Compraron en últimos 30 días
            </p>
          </Card>
        </div>
      )}

      {/* Form or Table */}
      {showForm ? (
        <Card className="p-6">
          <h2 className="text-xl font-semibold mb-6">
            {editingCustomer ? 'Editar Cliente' : 'Crear Nuevo Cliente'}
          </h2>
          <CustomerForm
            customer={editingCustomer || undefined}
            onSubmit={handleSubmit}
            onCancel={handleCancel}
            isLoading={createMutation.isPending || updateMutation.isPending}
          />
        </Card>
      ) : (
        <>
          {/* Filters */}
          <CustomerFilters onFilterChange={handleFilterChange} />

          {/* Customers Table */}
          <Card className="overflow-hidden">
            <CustomersTable
              customers={customersData?.data || []}
              onEdit={handleEdit}
              onDelete={handleDelete}
              onView={handleView}
              onManageLoyalty={handleManageLoyalty}
              isLoading={isLoading}
            />

            {/* Pagination */}
            {customersData && customersData.total > 0 && (
              <div className="px-6 py-4 border-t border-gray-200 flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  Mostrando {((customersData.page - 1) * customersData.pageSize) + 1} -{' '}
                  {Math.min(
                    customersData.page * customersData.pageSize,
                    customersData.total
                  )}{' '}
                  de {customersData.total} clientes
                </div>
                <div className="flex gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    disabled={customersData.page === 1}
                    onClick={() =>
                      setQueryParams((prev) => ({ ...prev, page: prev.page! - 1 }))
                    }
                  >
                    Anterior
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    disabled={
                      customersData.page >=
                      Math.ceil(customersData.total / customersData.pageSize)
                    }
                    onClick={() =>
                      setQueryParams((prev) => ({ ...prev, page: prev.page! + 1 }))
                    }
                  >
                    Siguiente
                  </Button>
                </div>
              </div>
            )}
          </Card>
        </>
      )}

      {/* Loyalty Points Modal */}
      <LoyaltyPointsModal
        isOpen={!!loyaltyModalCustomer}
        onClose={() => setLoyaltyModalCustomer(null)}
        customer={loyaltyModalCustomer}
        onConfirm={handleLoyaltyConfirm}
        isLoading={updateLoyaltyMutation.isPending}
      />
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Package, TrendingUp, AlertTriangle, Warehouse as WarehouseIcon } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { StockTable } from '@/components/inventory/StockTable';
import { StockMovements } from '@/components/inventory/StockMovements';
import { AdjustStockModal } from '@/components/inventory/AdjustStockModal';
import { InventoryFilters } from '@/components/inventory/InventoryFilters';
import { useToolbar } from '@/components/layout/ToolbarContext';
import {
  useStock,
  useMovements,
  useWarehouses,
  useInventoryStats,
  useAdjustStock,
} from '@/hooks/useInventory';
import type { StockLevel, StockQueryParams, MovementQueryParams } from '@/types/inventory';

export default function InventoryPage() {
  const { setToolbar } = useToolbar();
  const [stockParams, setStockParams] = useState<StockQueryParams>({
    page: 1,
    pageSize: 20,
  });
  const [movementParams, setMovementParams] = useState<MovementQueryParams>({
    page: 1,
    pageSize: 10,
  });
  const [adjustingStock, setAdjustingStock] = useState<StockLevel | null>(null);
  const [activeTab, setActiveTab] = useState<'stock' | 'movements'>('stock');

  // Set toolbar config
  useEffect(() => {
    setToolbar({ title: 'Inventario' });
  }, [setToolbar]);

  // Queries
  const { data: stockData, isLoading: stockLoading } = useStock(stockParams);
  const { data: movementsData, isLoading: movementsLoading } = useMovements(movementParams);
  const { data: warehouses } = useWarehouses();
  const { data: stats } = useInventoryStats();

  // Mutations
  const adjustStockMutation = useAdjustStock();

  const handleFilterChange = (filters: StockQueryParams) => {
    setStockParams({ ...filters, page: 1, pageSize: 20 });
  };

  const handleAdjustStock = (stock: StockLevel) => {
    setAdjustingStock(stock);
  };

  const handleConfirmAdjust = async (
    movementType: 'IN' | 'OUT' | 'ADJUST' | 'DAMAGE' | 'RETURN',
    quantity: number,
    referenceNumber?: string,
    notes?: string
  ) => {
    if (!adjustingStock) return;

    try {
      await adjustStockMutation.mutateAsync({
        product_id: adjustingStock.product_id,
        warehouse_id: adjustingStock.warehouse_id,
        location_id: adjustingStock.location_id,
        movement_type: movementType,
        quantity,
        reference_number: referenceNumber,
        notes,
      });

      alert('Stock ajustado exitosamente');
      setAdjustingStock(null);
    } catch (error) {
      alert('Error al ajustar el stock');
      console.error(error);
    }
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Inventario</h1>
        <p className="text-gray-500 mt-1">Gestiona tu stock y movimientos de inventario</p>
      </div>

      {/* Stats Cards */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Total Productos</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalProducts}
                </p>
              </div>
              <Package className="w-10 h-10 text-blue-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              En inventario
            </p>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Stock Total</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalStock.toLocaleString()}
                </p>
              </div>
              <TrendingUp className="w-10 h-10 text-green-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Unidades en stock
            </p>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Stock Bajo</p>
                <p className="text-2xl font-bold text-yellow-600 mt-1">
                  {stats.lowStockItems}
                </p>
              </div>
              <AlertTriangle className="w-10 h-10 text-yellow-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Requieren reabastecimiento
            </p>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Almacenes</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalWarehouses}
                </p>
              </div>
              <WarehouseIcon className="w-10 h-10 text-purple-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Activos
            </p>
          </Card>
        </div>
      )}

      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="flex gap-8">
          <button
            onClick={() => setActiveTab('stock')}
            className={`
              pb-4 border-b-2 font-medium transition-colors
              ${
                activeTab === 'stock'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700'
              }
            `}
          >
            Stock Actual
          </button>
          <button
            onClick={() => setActiveTab('movements')}
            className={`
              pb-4 border-b-2 font-medium transition-colors
              ${
                activeTab === 'movements'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700'
              }
            `}
          >
            Movimientos
            {movementsData && movementsData.total > 0 && (
              <Badge variant="default" className="ml-2">
                {movementsData.total}
              </Badge>
            )}
          </button>
        </nav>
      </div>

      {/* Content */}
      {activeTab === 'stock' && (
        <>
          {/* Filters */}
          <InventoryFilters
            onFilterChange={handleFilterChange}
            warehouses={warehouses || []}
          />

          {/* Stock Table */}
          <Card className="overflow-hidden">
            <StockTable
              stock={stockData?.data || []}
              onAdjust={handleAdjustStock}
              isLoading={stockLoading}
            />

            {/* Pagination */}
            {stockData && stockData.total > 0 && (
              <div className="px-6 py-4 border-t border-gray-200 flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  Mostrando {((stockData.page - 1) * stockData.pageSize) + 1} -{' '}
                  {Math.min(stockData.page * stockData.pageSize, stockData.total)}{' '}
                  de {stockData.total} productos
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() =>
                      setStockParams((prev) => ({ ...prev, page: prev.page! - 1 }))
                    }
                    disabled={stockData.page === 1}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Anterior
                  </button>
                  <button
                    onClick={() =>
                      setStockParams((prev) => ({ ...prev, page: prev.page! + 1 }))
                    }
                    disabled={
                      stockData.page >= Math.ceil(stockData.total / stockData.pageSize)
                    }
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Siguiente
                  </button>
                </div>
              </div>
            )}
          </Card>
        </>
      )}

      {activeTab === 'movements' && (
        <>
          {/* Movements List */}
          <Card className="p-6">
            <StockMovements
              movements={movementsData?.data || []}
              isLoading={movementsLoading}
            />

            {/* Pagination */}
            {movementsData && movementsData.total > 0 && (
              <div className="mt-6 pt-6 border-t border-gray-200 flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  Mostrando {((movementsData.page - 1) * movementsData.pageSize) + 1} -{' '}
                  {Math.min(
                    movementsData.page * movementsData.pageSize,
                    movementsData.total
                  )}{' '}
                  de {movementsData.total} movimientos
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() =>
                      setMovementParams((prev) => ({ ...prev, page: prev.page! - 1 }))
                    }
                    disabled={movementsData.page === 1}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Anterior
                  </button>
                  <button
                    onClick={() =>
                      setMovementParams((prev) => ({ ...prev, page: prev.page! + 1 }))
                    }
                    disabled={
                      movementsData.page >=
                      Math.ceil(movementsData.total / movementsData.pageSize)
                    }
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Siguiente
                  </button>
                </div>
              </div>
            )}
          </Card>
        </>
      )}

      {/* Adjust Stock Modal */}
      <AdjustStockModal
        isOpen={!!adjustingStock}
        onClose={() => setAdjustingStock(null)}
        stock={adjustingStock}
        onConfirm={handleConfirmAdjust}
        isLoading={adjustStockMutation.isPending}
      />
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { UsersTable } from '@/components/users/UsersTable';
import { UserForm } from '@/components/users/UserForm';
import { AssignRolesModal } from '@/components/users/AssignRolesModal';
import { ResetPasswordModal } from '@/components/users/ResetPasswordModal';
import { useToolbar } from '@/components/layout/ToolbarContext';
import {
  useUsers,
  useUserStats,
  useCreateUser,
  useUpdateUser,
  useAssignRoles,
  useToggleUserStatus,
  useResetPassword,
  useDeleteUser,
} from '@/hooks/useUsers';
import type { UserResponse, UserQuery, CreateUserDto, UpdateUserDto } from '@/types/users';
import { Users, UserPlus, UserCheck, UserX, Search } from 'lucide-react';

type ModalType = 'create' | 'edit' | 'assign-roles' | 'reset-password' | null;

export default function UsersPage() {
  const { setToolbar } = useToolbar();
  const [query, setQuery] = useState<UserQuery>({ page: 1, pageSize: 20 });
  const [search, setSearch] = useState('');
  const [roleFilter, setRoleFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');

  // Set toolbar config
  useEffect(() => {
    setToolbar({ title: 'Usuarios' });
  }, [setToolbar]);
  const [modal, setModal] = useState<ModalType>(null);
  const [selectedUser, setSelectedUser] = useState<UserResponse | null>(null);

  const { data: usersData, isLoading } = useUsers(query);
  const { data: stats } = useUserStats();

  const createUser = useCreateUser();
  const updateUser = useUpdateUser();
  const assignRoles = useAssignRoles();
  const toggleStatus = useToggleUserStatus();
  const resetPassword = useResetPassword();
  const deleteUser = useDeleteUser();

  const handleSearch = () => {
    setQuery((prev) => ({
      ...prev,
      page: 1,
      search: search || undefined,
      role: roleFilter || undefined,
      isActive: statusFilter === '' ? undefined : statusFilter === 'active',
    }));
  };

  const handleResetFilters = () => {
    setSearch('');
    setRoleFilter('');
    setStatusFilter('');
    setQuery({ page: 1, pageSize: 20 });
  };

  const openCreate = () => { setSelectedUser(null); setModal('create'); };
  const openEdit = (user: UserResponse) => { setSelectedUser(user); setModal('edit'); };
  const openAssignRoles = (user: UserResponse) => { setSelectedUser(user); setModal('assign-roles'); };
  const openResetPassword = (user: UserResponse) => { setSelectedUser(user); setModal('reset-password'); };
  const closeModal = () => { setModal(null); setSelectedUser(null); };

  const handleCreate = async (dto: CreateUserDto | UpdateUserDto) => {
    await createUser.mutateAsync(dto as CreateUserDto);
    closeModal();
  };

  const handleUpdate = async (dto: CreateUserDto | UpdateUserDto) => {
    if (!selectedUser) return;
    await updateUser.mutateAsync({ id: selectedUser.id, dto: dto as UpdateUserDto });
    closeModal();
  };

  const handleAssignRoles = async (roleIds: string[]) => {
    if (!selectedUser) return;
    await assignRoles.mutateAsync({ id: selectedUser.id, dto: { roleIds } });
    closeModal();
  };

  const handleResetPassword = async (newPassword: string) => {
    if (!selectedUser) return;
    await resetPassword.mutateAsync({ id: selectedUser.id, dto: { newPassword } });
    closeModal();
  };

  const handleToggleStatus = async (user: UserResponse) => {
    if (confirm(`¿${user.isActive ? 'Desactivar' : 'Activar'} usuario "${user.name}"?`)) {
      await toggleStatus.mutateAsync(user.id);
    }
  };

  const handleDelete = async (user: UserResponse) => {
    if (confirm(`¿Eliminar usuario "${user.name}"? Esta acción no se puede deshacer.`)) {
      await deleteUser.mutateAsync(user.id);
    }
  };

  const handlePageChange = (newPage: number) => {
    setQuery((prev) => ({ ...prev, page: newPage }));
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-gray-900">Gestión de Usuarios</h1>
          <p className="text-gray-600 mt-1">Administra usuarios, roles y permisos del sistema</p>
        </div>
        <Button onClick={openCreate}>
          <UserPlus className="h-4 w-4 mr-2" />
          Nuevo Usuario
        </Button>
      </div>

      {/* Stats Cards */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-gray-600">Total Usuarios</CardTitle>
              <Users className="h-4 w-4 text-gray-400" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.totalUsers}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-gray-600">Activos</CardTitle>
              <UserCheck className="h-4 w-4 text-green-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">{stats.activeUsers}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-gray-600">Inactivos</CardTitle>
              <UserX className="h-4 w-4 text-gray-400" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-gray-500">{stats.inactiveUsers}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-gray-600">Nuevos (mes)</CardTitle>
              <UserPlus className="h-4 w-4 text-blue-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-blue-600">{stats.newUsersThisMonth}</div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Roles summary */}
      {stats && stats.usersByRole.length > 0 && (
        <div className="flex gap-3 flex-wrap">
          {stats.usersByRole.map((r) => (
            <div key={r.roleName} className="flex items-center gap-2 px-3 py-1 bg-gray-100 rounded-full text-sm">
              <span className="font-medium">{r.roleName}</span>
              <span className="text-gray-500">{r.count}</span>
            </div>
          ))}
        </div>
      )}

      {/* Filters */}
      <Card>
        <CardContent className="pt-4">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-3 items-end">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-gray-400" />
              <Input
                placeholder="Buscar por nombre o email..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
                className="pl-10"
              />
            </div>
            <select
              value={roleFilter}
              onChange={(e) => setRoleFilter(e.target.value)}
              className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm"
            >
              <option value="">Todos los roles</option>
              <option value="admin">Admin</option>
              <option value="manager">Manager</option>
              <option value="staff">Staff</option>
              <option value="cashier">Cashier</option>
            </select>
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm"
            >
              <option value="">Todos los estados</option>
              <option value="active">Activos</option>
              <option value="inactive">Inactivos</option>
            </select>
            <div className="flex gap-2">
              <Button onClick={handleSearch} className="flex-1">Buscar</Button>
              <Button variant="outline" onClick={handleResetFilters}>Limpiar</Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Table */}
      <Card>
        <CardContent className="pt-4">
          <UsersTable
            users={usersData?.data || []}
            isLoading={isLoading}
            onEdit={openEdit}
            onAssignRoles={openAssignRoles}
            onResetPassword={openResetPassword}
            onToggleStatus={handleToggleStatus}
            onDelete={handleDelete}
          />

          {/* Pagination */}
          {usersData && usersData.totalPages > 1 && (
            <div className="flex items-center justify-between mt-4 pt-4 border-t">
              <p className="text-sm text-gray-500">
                {usersData.total} usuarios · Página {usersData.page} de {usersData.totalPages}
              </p>
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  disabled={usersData.page <= 1}
                  onClick={() => handlePageChange(usersData.page - 1)}
                >
                  Anterior
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  disabled={usersData.page >= usersData.totalPages}
                  onClick={() => handlePageChange(usersData.page + 1)}
                >
                  Siguiente
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Modals */}
      {modal === 'create' && (
        <UserForm
          onSubmit={handleCreate}
          onCancel={closeModal}
          isLoading={createUser.isPending}
        />
      )}
      {modal === 'edit' && selectedUser && (
        <UserForm
          user={selectedUser}
          onSubmit={handleUpdate}
          onCancel={closeModal}
          isLoading={updateUser.isPending}
        />
      )}
      {modal === 'assign-roles' && selectedUser && (
        <AssignRolesModal
          user={selectedUser}
          onSubmit={handleAssignRoles}
          onCancel={closeModal}
          isLoading={assignRoles.isPending}
        />
      )}
      {modal === 'reset-password' && selectedUser && (
        <ResetPasswordModal
          user={selectedUser}
          onSubmit={handleResetPassword}
          onCancel={closeModal}
          isLoading={resetPassword.isPending}
        />
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { useToolbar } from '@/components/layout/ToolbarContext';
import {
  useNotifications,
  useMarkAsRead,
  useMarkAllAsRead,
  useClearRead,
  useRemoveNotification,
} from '@/hooks/useNotifications';
import { Bell, CheckCheck, Trash2, X, Filter } from 'lucide-react';
import type { NotificationQuery, Notification, NotificationType } from '@/types/notifications';

const PRIORITY_STYLES: Record<string, { dot: string; badge: string }> = {
  critical: { dot: 'bg-red-500', badge: 'bg-red-100 text-red-700 border-red-200' },
  high:     { dot: 'bg-orange-500', badge: 'bg-orange-100 text-orange-700 border-orange-200' },
  medium:   { dot: 'bg-blue-500', badge: 'bg-blue-100 text-blue-700 border-blue-200' },
  low:      { dot: 'bg-gray-400', badge: 'bg-gray-100 text-gray-600 border-gray-200' },
};

const TYPE_ICONS: Record<string, string> = {
  low_stock: '📦', out_of_stock: '⚠️', sale_milestone: '🎯',
  new_user: '👤', system: '⚙️', reorder_alert: '🔔', large_sale: '💰',
};

const TYPE_LABELS: Record<string, string> = {
  low_stock: 'Stock bajo', out_of_stock: 'Sin stock', sale_milestone: 'Hito de ventas',
  new_user: 'Nuevo usuario', system: 'Sistema', reorder_alert: 'Reorden', large_sale: 'Venta grande',
};

function timeAgo(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime();
  const min = Math.floor(diff / 60000);
  if (min < 1) return 'Ahora mismo';
  if (min < 60) return `hace ${min} min`;
  const h = Math.floor(min / 60);
  if (h < 24) return `hace ${h}h`;
  return `hace ${Math.floor(h / 24)}d`;
}

export default function NotificationsPage() {
  const { setToolbar } = useToolbar();
  const [query, setQuery] = useState<NotificationQuery>({ page: 1, pageSize: 20 });
  const [typeFilter, setTypeFilter] = useState('');
  const [unreadOnly, setUnreadOnly] = useState(false);

  // Set toolbar config
  useEffect(() => {
    setToolbar({ title: 'Notificaciones' });
  }, [setToolbar]);

  const { data: list, isLoading } = useNotifications(query);
  const markAsRead = useMarkAsRead();
  const markAllAsRead = useMarkAllAsRead();
  const clearRead = useClearRead();
  const remove = useRemoveNotification();

  const applyFilters = () => {
    setQuery({
      page: 1,
      pageSize: 20,
      type: typeFilter ? (typeFilter as NotificationType) : undefined,
      unreadOnly: unreadOnly || undefined,
    });
  };

  const notifications = list?.data || [];

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-gray-900">Notificaciones</h1>
          <p className="text-gray-600 mt-1">
            {list?.unreadCount
              ? `${list.unreadCount} sin leer · ${list.total} total`
              : `${list?.total || 0} notificaciones`}
          </p>
        </div>
        <div className="flex gap-2">
          {(list?.unreadCount || 0) > 0 && (
            <Button variant="outline" onClick={() => markAllAsRead.mutate()}>
              <CheckCheck className="h-4 w-4 mr-2" />
              Marcar todas leídas
            </Button>
          )}
          <Button variant="outline" onClick={() => clearRead.mutate()} className="text-red-600 hover:text-red-700">
            <Trash2 className="h-4 w-4 mr-2" />
            Limpiar leídas
          </Button>
        </div>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="pt-4">
          <div className="flex gap-3 items-center flex-wrap">
            <Filter className="h-4 w-4 text-gray-400" />
            <select
              value={typeFilter}
              onChange={(e) => setTypeFilter(e.target.value)}
              className="h-9 rounded-md border border-input bg-background px-3 text-sm"
            >
              <option value="">Todos los tipos</option>
              {Object.entries(TYPE_LABELS).map(([val, label]) => (
                <option key={val} value={val}>{label}</option>
              ))}
            </select>
            <label className="flex items-center gap-2 text-sm cursor-pointer">
              <input
                type="checkbox"
                checked={unreadOnly}
                onChange={(e) => setUnreadOnly(e.target.checked)}
                className="h-4 w-4"
              />
              Solo sin leer
            </label>
            <Button size="sm" onClick={applyFilters}>Aplicar</Button>
            <Button size="sm" variant="outline" onClick={() => {
              setTypeFilter('');
              setUnreadOnly(false);
              setQuery({ page: 1, pageSize: 20 });
            }}>
              Limpiar
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Notifications list */}
      <Card>
        <CardContent className="pt-0">
          {isLoading ? (
            <div className="space-y-3 pt-4">
              {[1, 2, 3, 4, 5].map((i) => (
                <div key={i} className="h-20 bg-gray-100 animate-pulse rounded" />
              ))}
            </div>
          ) : notifications.length === 0 ? (
            <div className="py-16 text-center text-gray-400">
              <Bell className="h-10 w-10 mx-auto mb-3 opacity-30" />
              <p className="text-lg font-medium">Sin notificaciones</p>
              <p className="text-sm mt-1">Aquí aparecerán alertas de stock, ventas y sistema</p>
            </div>
          ) : (
            <div className="divide-y">
              {notifications.map((notif: Notification) => {
                const priority = PRIORITY_STYLES[notif.priority] || PRIORITY_STYLES.low;
                return (
                  <div
                    key={notif.id}
                    className={`flex gap-4 py-4 px-4 hover:bg-gray-50 transition-colors ${
                      !notif.isRead ? 'bg-blue-50/20' : ''
                    }`}
                  >
                    {/* Priority indicator */}
                    <div className="flex-shrink-0 mt-1">
                      <span className={`inline-block h-3 w-3 rounded-full ${priority.dot}`} />
                    </div>

                    {/* Content */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-start justify-between gap-3">
                        <div>
                          <p className={`font-medium ${notif.isRead ? 'text-gray-600' : 'text-gray-900'}`}>
                            {TYPE_ICONS[notif.type]} {notif.title}
                          </p>
                          <p className="text-sm text-gray-500 mt-1">{notif.message}</p>
                        </div>
                        <div className="flex items-center gap-2 flex-shrink-0">
                          <span className={`hidden sm:inline-flex items-center px-2 py-0.5 rounded-full text-xs border ${priority.badge}`}>
                            {notif.priority}
                          </span>
                          {!notif.isRead && (
                            <span className="h-2 w-2 rounded-full bg-blue-500 flex-shrink-0" />
                          )}
                        </div>
                      </div>
                      <div className="flex items-center gap-4 mt-2">
                        <span className="text-xs text-gray-400">{timeAgo(notif.createdAt)}</span>
                        <span className="text-xs text-gray-400">{TYPE_LABELS[notif.type]}</span>
                        {!notif.isRead && (
                          <button
                            onClick={() => markAsRead.mutate(notif.id)}
                            className="text-xs text-blue-600 hover:underline"
                          >
                            Marcar leída
                          </button>
                        )}
                        <button
                          onClick={() => remove.mutate(notif.id)}
                          className="text-xs text-red-500 hover:underline ml-auto"
                        >
                          Eliminar
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}

          {/* Pagination */}
          {list && list.totalPages > 1 && (
            <div className="flex items-center justify-between pt-4 border-t mt-2">
              <p className="text-sm text-gray-500">
                Página {list.page} de {list.totalPages}
              </p>
              <div className="flex gap-2">
                <Button variant="outline" size="sm" disabled={list.page <= 1}
                  onClick={() => setQuery((q) => ({ ...q, page: (q.page || 1) - 1 }))}>
                  Anterior
                </Button>
                <Button variant="outline" size="sm" disabled={list.page >= list.totalPages}
                  onClick={() => setQuery((q) => ({ ...q, page: (q.page || 1) + 1 }))}>
                  Siguiente
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

