@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 240 10% 3.9%;
    --card: 0 0% 100%;
    --card-foreground: 240 10% 3.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 240 10% 3.9%;
    --primary: 221 83% 53%;
    --primary-foreground: 0 0% 98%;
    --secondary: 240 4.8% 95.9%;
    --secondary-foreground: 240 5.9% 10%;
    --muted: 240 4.8% 95.9%;
    --muted-foreground: 240 3.8% 46.1%;
    --accent: 240 4.8% 95.9%;
    --accent-foreground: 240 5.9% 10%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 98%;
    --border: 240 5.9% 90%;
    --input: 240 5.9% 90%;
    --ring: 221 83% 53%;
    --radius: 0.5rem;
  }

  .dark {
    /* Background Layers */
    --background: 222 16% 8%;
    --surface-1: 222 14% 11%;
    --surface-2: 222 12% 14%;
    --surface-3: 222 10% 17%;

    /* Text Hierarchy */
    --foreground: 220 10% 97%;
    --text-primary: 220 10% 97%;
    --text-secondary: 220 8% 80%;
    --text-tertiary: 220 6% 60%;
    --text-quaternary: 220 5% 45%;
    --text-disabled: 220 4% 35%;

    /* Brand Colors */
    --primary: 217 91% 60%;
    --primary-hover: 217 91% 55%;
    --primary-active: 217 91% 50%;
    --primary-foreground: 220 10% 97%;
    --primary-subtle: 217 91% 20%;
    --primary-muted: 217 50% 30%;

    --accent: 265 80% 65%;
    --accent-hover: 265 80% 60%;
    --accent-foreground: 220 10% 97%;
    --accent-subtle: 265 50% 25%;

    /* Semantic Colors */
    --success: 142 76% 45%;
    --success-hover: 142 76% 40%;
    --success-foreground: 220 10% 97%;
    --success-subtle: 142 40% 22%;

    --warning: 38 92% 50%;
    --warning-hover: 38 92% 45%;
    --warning-foreground: 222 16% 8%;
    --warning-subtle: 38 60% 25%;

    --error: 0 84% 60%;
    --error-hover: 0 84% 55%;
    --error-foreground: 220 10% 97%;
    --error-subtle: 0 60% 25%;

    --info: 199 89% 48%;
    --info-hover: 199 89% 43%;
    --info-foreground: 220 10% 97%;
    --info-subtle: 199 60% 22%;

    /* Legacy Compatibility */
    --destructive: 0 84% 60%;
    --destructive-foreground: 220 10% 97%;
    --secondary: 222 12% 14%;
    --secondary-foreground: 220 10% 97%;
    --muted: 220 6% 60%;
    --muted-foreground: 220 8% 80%;

    /* Borders */
    --border-strong: 220 8% 20%;
    --border: 220 6% 16%;
    --border-subtle: 220 5% 14%;
    --divider: 220 6% 16%;

    /* Interactive States */
    --hover-bg: 222 12% 16%;
    --active-bg: 222 12% 18%;
    --selected-bg: 217 50% 20%;
    --disabled-bg: 222 12% 12%;

    /* UI Elements */
    --card: 222 14% 11%;
    --card-foreground: 220 10% 97%;
    --popover: 222 12% 14%;
    --popover-foreground: 220 10% 97%;
    --input: 220 6% 16%;
    --ring: 217 91% 60%;
  }
}

@layer base {
  * {
    @apply border-border;
    @apply transition-colors duration-200;
  }
  body {
    @apply bg-background text-foreground;
  }
}

