'use client';

import { useState } from 'react';
import { X, Award, Plus, Minus, Edit } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import type { Customer } from '@/types/customer';

interface LoyaltyPointsModalProps {
  isOpen: boolean;
  onClose: () => void;
  customer: Customer | null;
  onConfirm: (operation: 'add' | 'subtract' | 'set', points: number) => void;
  isLoading?: boolean;
}

export function LoyaltyPointsModal({
  isOpen,
  onClose,
  customer,
  onConfirm,
  isLoading,
}: LoyaltyPointsModalProps) {
  const [operation, setOperation] = useState<'add' | 'subtract' | 'set'>('add');
  const [points, setPoints] = useState<number>(0);

  if (!isOpen || !customer) return null;

  const handleConfirm = () => {
    if (points <= 0) {
      alert('Ingresa una cantidad válida de puntos');
      return;
    }

    if (operation === 'subtract' && points > customer.loyalty_points) {
      alert('No hay suficientes puntos para restar');
      return;
    }

    onConfirm(operation, points);
    setPoints(0);
  };

  const getNewBalance = () => {
    switch (operation) {
      case 'add':
        return customer.loyalty_points + points;
      case 'subtract':
        return customer.loyalty_points - points;
      case 'set':
        return points;
      default:
        return customer.loyalty_points;
    }
  };

  const operations = [
    {
      id: 'add' as const,
      label: 'Agregar',
      icon: Plus,
      color: 'green',
      description: 'Sumar puntos al balance actual',
    },
    {
      id: 'subtract' as const,
      label: 'Restar',
      icon: Minus,
      color: 'red',
      description: 'Restar puntos del balance actual',
    },
    {
      id: 'set' as const,
      label: 'Establecer',
      icon: Edit,
      color: 'blue',
      description: 'Definir un nuevo balance exacto',
    },
  ];

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-md max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <div className="flex items-center gap-3">
            <Award className="w-6 h-6 text-yellow-500" />
            <h2 className="text-2xl font-bold text-gray-900">Gestionar Puntos</h2>
          </div>
          <button
            onClick={onClose}
            className="text-quaternary hover:text-secondary transition-colors"
            disabled={isLoading}
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 space-y-6">
          {/* Customer info */}
          <div className="bg-gray-50 rounded-lg p-4">
            <p className="text-sm text-secondary mb-1">Cliente</p>
            <p className="text-lg font-semibold text-gray-900">{customer.name}</p>
            <div className="mt-3 flex items-center gap-2">
              <span className="text-sm text-secondary">Balance actual:</span>
              <span className="text-xl font-bold text-yellow-600">
                {customer.loyalty_points} puntos
              </span>
            </div>
          </div>

          {/* Operation selection */}
          <div>
            <Label className="mb-3">Operación</Label>
            <div className="grid grid-cols-3 gap-3">
              {operations.map((op) => {
                const Icon = op.icon;
                const isSelected = operation === op.id;
                return (
                  <button
                    key={op.id}
                    onClick={() => setOperation(op.id)}
                    className={`
                      p-4 rounded-lg border-2 transition-all text-center
                      ${
                        isSelected
                          ? `border-${op.color}-500 bg-${op.color}-50`
                          : 'border-gray-200 hover:border-gray-300'
                      }
                    `}
                  >
                    <Icon
                      className={`w-6 h-6 mx-auto mb-2 ${
                        isSelected ? `text-${op.color}-600` : 'text-quaternary'
                      }`}
                    />
                    <p
                      className={`text-sm font-medium ${
                        isSelected ? `text-${op.color}-900` : 'text-secondary'
                      }`}
                    >
                      {op.label}
                    </p>
                  </button>
                );
              })}
            </div>
            <p className="text-xs text-tertiary mt-2">
              {operations.find((op) => op.id === operation)?.description}
            </p>
          </div>

          {/* Points input */}
          <div>
            <Label htmlFor="points">
              {operation === 'set' ? 'Nuevo balance' : 'Cantidad de puntos'}
            </Label>
            <Input
              id="points"
              type="number"
              min="0"
              value={points || ''}
              onChange={(e) => setPoints(parseInt(e.target.value) || 0)}
              placeholder="0"
            />
          </div>

          {/* Preview */}
          {points > 0 && (
            <div className="bg-blue-50 rounded-lg p-4 border border-blue-200">
              <p className="text-sm text-blue-700 mb-2">Vista previa</p>
              <div className="flex items-center justify-between">
                <span className="text-sm text-blue-600">Balance actual:</span>
                <span className="font-semibold text-blue-900">
                  {customer.loyalty_points}
                </span>
              </div>
              {operation !== 'set' && (
                <div className="flex items-center justify-between mt-1">
                  <span className="text-sm text-blue-600">
                    {operation === 'add' ? 'Sumar:' : 'Restar:'}
                  </span>
                  <span
                    className={`font-semibold ${
                      operation === 'add' ? 'text-green-600' : 'text-red-600'
                    }`}
                  >
                    {operation === 'add' ? '+' : '-'}
                    {points}
                  </span>
                </div>
              )}
              <div className="flex items-center justify-between mt-2 pt-2 border-t border-blue-300">
                <span className="text-sm font-semibold text-blue-700">
                  Nuevo balance:
                </span>
                <span className="text-xl font-bold text-blue-900">
                  {getNewBalance()}
                </span>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex gap-3 p-6 border-t bg-gray-50">
          <Button
            variant="outline"
            onClick={onClose}
            disabled={isLoading}
            className="flex-1"
          >
            Cancelar
          </Button>
          <Button
            onClick={handleConfirm}
            disabled={isLoading || points <= 0}
            className="flex-1"
          >
            {isLoading ? 'Procesando...' : 'Confirmar'}
          </Button>
        </div>
      </div>
    </div>
  );
}

'use client';

import { Pencil, Trash2, Eye, Award } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import type { Customer } from '@/types/customer';

interface CustomersTableProps {
  customers: Customer[];
  onEdit: (customer: Customer) => void;
  onDelete: (customer: Customer) => void;
  onView: (customer: Customer) => void;
  onManageLoyalty: (customer: Customer) => void;
  isLoading?: boolean;
}

export function CustomersTable({
  customers,
  onEdit,
  onDelete,
  onView,
  onManageLoyalty,
  isLoading,
}: CustomersTableProps) {
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('es-CO', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    }).format(date);
  };

  if (isLoading) {
    return (
      <div className="animate-pulse space-y-4">
        <div className="h-12 bg-gray-200 rounded"></div>
        <div className="h-12 bg-gray-200 rounded"></div>
        <div className="h-12 bg-gray-200 rounded"></div>
      </div>
    );
  }

  if (customers.length === 0) {
    return (
      <div className="text-center py-12">
        <p className="text-tertiary text-lg">No se encontraron clientes</p>
        <p className="text-quaternary text-sm mt-2">
          Intenta ajustar los filtros o crear un nuevo cliente
        </p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse">
        <thead>
          <tr className="border-b border-gray-200 bg-gray-50">
            <th className="text-left p-4 font-semibold text-secondary">Cliente</th>
            <th className="text-left p-4 font-semibold text-secondary">Contacto</th>
            <th className="text-right p-4 font-semibold text-secondary">Compras</th>
            <th className="text-center p-4 font-semibold text-secondary">Puntos</th>
            <th className="text-center p-4 font-semibold text-secondary">Última compra</th>
            <th className="text-center p-4 font-semibold text-secondary">Estado</th>
            <th className="text-center p-4 font-semibold text-secondary">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer) => (
            <tr
              key={customer.id}
              className="border-b border-gray-100 hover:bg-gray-50 transition-colors"
            >
              <td className="p-4">
                <div>
                  <div className="font-medium text-gray-900">{customer.name}</div>
                  {customer.address && (
                    <div className="text-sm text-tertiary truncate max-w-xs">
                      {customer.address}
                    </div>
                  )}
                </div>
              </td>
              <td className="p-4">
                <div className="text-sm">
                  {customer.email && (
                    <div className="text-secondary">{customer.email}</div>
                  )}
                  {customer.phone && (
                    <div className="text-tertiary">{customer.phone}</div>
                  )}
                  {!customer.email && !customer.phone && (
                    <div className="text-quaternary">-</div>
                  )}
                </div>
              </td>
              <td className="p-4 text-right">
                <div>
                  <div className="font-semibold text-gray-900">
                    {formatCurrency(customer.total_purchases)}
                  </div>
                  <div className="text-sm text-tertiary">Total</div>
                </div>
              </td>
              <td className="p-4 text-center">
                <div className="inline-flex items-center gap-1">
                  <Award className="w-4 h-4 text-yellow-500" />
                  <span className="font-semibold text-gray-900">
                    {customer.loyalty_points}
                  </span>
                </div>
              </td>
              <td className="p-4 text-center">
                <span className="text-sm text-secondary">
                  {formatDate(customer.last_purchase_date)}
                </span>
              </td>
              <td className="p-4 text-center">
                {customer.is_active ? (
                  <Badge variant="success">Activo</Badge>
                ) : (
                  <Badge variant="secondary">Inactivo</Badge>
                )}
              </td>
              <td className="p-4">
                <div className="flex items-center justify-center gap-2">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onView(customer)}
                    title="Ver detalles"
                  >
                    <Eye className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onManageLoyalty(customer)}
                    title="Gestionar puntos"
                    className="text-yellow-600 hover:text-yellow-700 hover:bg-yellow-50"
                  >
                    <Award className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(customer)}
                    title="Editar"
                  >
                    <Pencil className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(customer)}
                    title="Eliminar"
                    className="text-red-600 hover:text-red-700 hover:bg-red-50"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { Bell, X, CheckCheck, Trash2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import {
  useUnreadCount,
  useNotifications,
  useMarkAsRead,
  useMarkAllAsRead,
  useClearRead,
} from '@/hooks/useNotifications';
import type { Notification } from '@/types/notifications';

const PRIORITY_COLORS: Record<string, string> = {
  critical: 'bg-red-500',
  high: 'bg-orange-500',
  medium: 'bg-blue-500',
  low: 'bg-quaternary',
};

const TYPE_ICONS: Record<string, string> = {
  low_stock: '📦',
  out_of_stock: '⚠️',
  sale_milestone: '🎯',
  new_user: '👤',
  system: '⚙️',
  reorder_alert: '🔔',
  large_sale: '💰',
};

function timeAgo(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime();
  const minutes = Math.floor(diff / 60000);
  if (minutes < 1) return 'Ahora';
  if (minutes < 60) return `hace ${minutes}m`;
  const hours = Math.floor(minutes / 60);
  if (hours < 24) return `hace ${hours}h`;
  const days = Math.floor(hours / 24);
  return `hace ${days}d`;
}

export function NotificationBell() {
  const [open, setOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  const { data: unreadData } = useUnreadCount();
  const { data: notifList } = useNotifications({ pageSize: 10 });
  const markAsRead = useMarkAsRead();
  const markAllAsRead = useMarkAllAsRead();
  const clearRead = useClearRead();

  const unreadCount = unreadData?.count || 0;
  const notifications = notifList?.data || [];

  // Close on outside click
  useEffect(() => {
    const handler = (e: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target as Node)) {
        setOpen(false);
      }
    };
    document.addEventListener('mousedown', handler);
    return () => document.removeEventListener('mousedown', handler);
  }, []);

  const handleMarkRead = (notif: Notification) => {
    if (!notif.isRead) markAsRead.mutate(notif.id);
  };

  return (
    <div className="relative" ref={dropdownRef}>
      {/* Bell Button */}
      <button
        onClick={() => setOpen((prev) => !prev)}
        className="relative p-2 rounded-lg hover:bg-gray-100 transition-colors"
        aria-label="Notificaciones"
      >
        <Bell className="h-5 w-5 text-secondary" />
        {unreadCount > 0 && (
          <span className="absolute -top-1 -right-1 flex h-5 w-5 items-center justify-center rounded-full bg-red-500 text-xs font-bold text-white">
            {unreadCount > 99 ? '99+' : unreadCount}
          </span>
        )}
      </button>

      {/* Dropdown */}
      {open && (
        <div className="absolute right-0 top-12 z-50 w-96 bg-white border border-gray-200 rounded-xl shadow-xl overflow-hidden">
          {/* Header */}
          <div className="flex items-center justify-between px-4 py-3 border-b bg-gray-50">
            <div>
              <h3 className="font-semibold text-gray-900">Notificaciones</h3>
              {unreadCount > 0 && (
                <p className="text-xs text-tertiary">{unreadCount} sin leer</p>
              )}
            </div>
            <div className="flex gap-1">
              {unreadCount > 0 && (
                <button
                  onClick={() => markAllAsRead.mutate()}
                  className="p-1.5 rounded hover:bg-gray-200 text-gray-500 hover:text-gray-700"
                  title="Marcar todas como leídas"
                >
                  <CheckCheck className="h-4 w-4" />
                </button>
              )}
              <button
                onClick={() => clearRead.mutate()}
                className="p-1.5 rounded hover:bg-gray-200 text-gray-500 hover:text-gray-700"
                title="Limpiar leídas"
              >
                <Trash2 className="h-4 w-4" />
              </button>
              <button
                onClick={() => setOpen(false)}
                className="p-1.5 rounded hover:bg-gray-200 text-gray-500"
              >
                <X className="h-4 w-4" />
              </button>
            </div>
          </div>

          {/* List */}
          <div className="max-h-96 overflow-y-auto">
            {notifications.length === 0 ? (
              <div className="py-12 text-center text-quaternary">
                <Bell className="h-8 w-8 mx-auto mb-2 opacity-40" />
                <p className="text-sm">Sin notificaciones</p>
              </div>
            ) : (
              notifications.map((notif) => (
                <div
                  key={notif.id}
                  onClick={() => handleMarkRead(notif)}
                  className={`flex gap-3 px-4 py-3 border-b cursor-pointer hover:bg-gray-50 transition-colors ${
                    notif.isRead ? 'opacity-60' : 'bg-blue-50/30'
                  }`}
                >
                  {/* Priority dot */}
                  <div className="mt-1 flex-shrink-0">
                    <span
                      className={`inline-block h-2 w-2 rounded-full ${
                        PRIORITY_COLORS[notif.priority] || 'bg-quaternary'
                      }`}
                    />
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-start justify-between gap-2">
                      <p className={`text-sm font-medium leading-tight ${notif.isRead ? 'text-secondary' : 'text-gray-900'}`}>
                        {TYPE_ICONS[notif.type]} {notif.title}
                      </p>
                      {!notif.isRead && (
                        <span className="flex-shrink-0 h-2 w-2 rounded-full bg-blue-500 mt-1" />
                      )}
                    </div>
                    <p className="text-xs text-tertiary mt-0.5 line-clamp-2">{notif.message}</p>
                    <p className="text-xs text-quaternary mt-1">{timeAgo(notif.createdAt)}</p>
                  </div>
                </div>
              ))
            )}
          </div>

          {/* Footer */}
          {notifications.length > 0 && (
            <div className="px-4 py-2 border-t bg-gray-50 text-center">
              <a
                href="/notifications"
                className="text-sm text-blue-600 hover:underline"
                onClick={() => setOpen(false)}
              >
                Ver todas las notificaciones
              </a>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

'use client';

import { usePathname, useRouter } from 'next/navigation';
import { ChevronLeft, Home } from 'lucide-react';
import { Button } from '@/components/ui/button';

interface PageToolbarProps {
  title: string;
  showBackButton?: boolean;
  backHref?: string;
  actions?: React.ReactNode;
}

const MAIN_SECTIONS = [
  '/dashboard',
  '/products',
  '/sales',
  '/customers',
  '/reports',
  '/settings',
  '/inventory',
  '/users',
  '/notifications',
];

export function PageToolbar({
  title,
  showBackButton,
  backHref = '/dashboard',
  actions,
}: PageToolbarProps) {
  const pathname = usePathname();
  const router = useRouter();

  const shouldShowBackButton = () => {
    if (showBackButton !== undefined) return showBackButton;

    const isExactMainSection = MAIN_SECTIONS.includes(pathname);
    const segmentCount = pathname.split('/').filter(Boolean).length;

    return segmentCount > 1 && !isExactMainSection;
  };

  const handleBack = () => {
    if (backHref) {
      router.push(backHref);
    } else {
      router.back();
    }
  };

  const handleHome = () => {
    router.push('/dashboard');
  };

  return (
    <div className="flex items-center justify-between px-6 py-3 border-b border-gray-200 bg-white">
      <div className="flex items-center gap-3">
        {shouldShowBackButton() && (
          <Button
            variant="ghost"
            size="sm"
            onClick={handleBack}
            className="gap-1"
          >
            <ChevronLeft className="h-4 w-4" />
            Volver
          </Button>
        )}

        <Button
          variant="ghost"
          size="sm"
          onClick={handleHome}
          className="gap-1"
        >
          <Home className="h-4 w-4" />
        </Button>

        <h1 className="text-lg font-semibold text-gray-900">{title}</h1>
      </div>

      {actions && <div className="flex items-center gap-2">{actions}</div>}
    </div>
  );
}

