'use client';

import { useState, useRef, useEffect } from 'react';
import { Bell, X, CheckCheck, Trash2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import {
  useUnreadCount,
  useNotifications,
  useMarkAsRead,
  useMarkAllAsRead,
  useClearRead,
} from '@/hooks/useNotifications';
import type { Notification } from '@/types/notifications';

const PRIORITY_COLORS: Record<string, string> = {
  critical: 'bg-red-500',
  high: 'bg-orange-500',
  medium: 'bg-blue-500',
  low: 'bg-quaternary',
};

const TYPE_ICONS: Record<string, string> = {
  low_stock: '📦',
  out_of_stock: '⚠️',
  sale_milestone: '🎯',
  new_user: '👤',
  system: '⚙️',
  reorder_alert: '🔔',
  large_sale: '💰',
};

function timeAgo(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime();
  const minutes = Math.floor(diff / 60000);
  if (minutes < 1) return 'Ahora';
  if (minutes < 60) return `hace ${minutes}m`;
  const hours = Math.floor(minutes / 60);
  if (hours < 24) return `hace ${hours}h`;
  const days = Math.floor(hours / 24);
  return `hace ${days}d`;
}

export function NotificationBell() {
  const [open, setOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  const { data: unreadData } = useUnreadCount();
  const { data: notifList } = useNotifications({ pageSize: 10 });
  const markAsRead = useMarkAsRead();
  const markAllAsRead = useMarkAllAsRead();
  const clearRead = useClearRead();

  const unreadCount = unreadData?.count || 0;
  const notifications = notifList?.data || [];

  // Close on outside click
  useEffect(() => {
    const handler = (e: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target as Node)) {
        setOpen(false);
      }
    };
    document.addEventListener('mousedown', handler);
    return () => document.removeEventListener('mousedown', handler);
  }, []);

  const handleMarkRead = (notif: Notification) => {
    if (!notif.isRead) markAsRead.mutate(notif.id);
  };

  return (
    <div className="relative" ref={dropdownRef}>
      {/* Bell Button */}
      <button
        onClick={() => setOpen((prev) => !prev)}
        className="relative p-2 rounded-lg hover:bg-gray-100 transition-colors"
        aria-label="Notificaciones"
      >
        <Bell className="h-5 w-5 text-secondary" />
        {unreadCount > 0 && (
          <span className="absolute -top-1 -right-1 flex h-5 w-5 items-center justify-center rounded-full bg-red-500 text-xs font-bold text-white">
            {unreadCount > 99 ? '99+' : unreadCount}
          </span>
        )}
      </button>

      {/* Dropdown */}
      {open && (
        <div className="absolute right-0 top-12 z-50 w-96 bg-white border border-gray-200 rounded-xl shadow-xl overflow-hidden">
          {/* Header */}
          <div className="flex items-center justify-between px-4 py-3 border-b bg-gray-50">
            <div>
              <h3 className="font-semibold">Notificaciones</h3>
              {unreadCount > 0 && (
                <p className="text-xs text-tertiary">{unreadCount} sin leer</p>
              )}
            </div>
            <div className="flex gap-1">
              {unreadCount > 0 && (
                <button
                  onClick={() => markAllAsRead.mutate()}
                  className="p-1.5 rounded hover:bg-gray-200 text-gray-500 hover:text-gray-700"
                  title="Marcar todas como leídas"
                >
                  <CheckCheck className="h-4 w-4" />
                </button>
              )}
              <button
                onClick={() => clearRead.mutate()}
                className="p-1.5 rounded hover:bg-gray-200 text-gray-500 hover:text-gray-700"
                title="Limpiar leídas"
              >
                <Trash2 className="h-4 w-4" />
              </button>
              <button
                onClick={() => setOpen(false)}
                className="p-1.5 rounded hover:bg-gray-200 text-gray-500"
              >
                <X className="h-4 w-4" />
              </button>
            </div>
          </div>

          {/* List */}
          <div className="max-h-96 overflow-y-auto">
            {notifications.length === 0 ? (
              <div className="py-12 text-center text-quaternary">
                <Bell className="h-8 w-8 mx-auto mb-2 opacity-40" />
                <p className="text-sm">Sin notificaciones</p>
              </div>
            ) : (
              notifications.map((notif) => (
                <div
                  key={notif.id}
                  onClick={() => handleMarkRead(notif)}
                  className={`flex gap-3 px-4 py-3 border-b cursor-pointer hover:bg-gray-50 transition-colors ${
                    notif.isRead ? 'opacity-60' : 'bg-blue-50/30'
                  }`}
                >
                  {/* Priority dot */}
                  <div className="mt-1 flex-shrink-0">
                    <span
                      className={`inline-block h-2 w-2 rounded-full ${
                        PRIORITY_COLORS[notif.priority] || 'bg-quaternary'
                      }`}
                    />
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-start justify-between gap-2">
                      <p className={`text-sm font-medium leading-tight ${notif.isRead ? 'text-secondary' : ''}`}>
                        {TYPE_ICONS[notif.type]} {notif.title}
                      </p>
                      {!notif.isRead && (
                        <span className="flex-shrink-0 h-2 w-2 rounded-full bg-blue-500 mt-1" />
                      )}
                    </div>
                    <p className="text-xs text-tertiary mt-0.5 line-clamp-2">{notif.message}</p>
                    <p className="text-xs text-quaternary mt-1">{timeAgo(notif.createdAt)}</p>
                  </div>
                </div>
              ))
            )}
          </div>

          {/* Footer */}
          {notifications.length > 0 && (
            <div className="px-4 py-2 border-t bg-gray-50 text-center">
              <a
                href="/notifications"
                className="text-sm text-blue-600 hover:underline"
                onClick={() => setOpen(false)}
              >
                Ver todas las notificaciones
              </a>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { X, ArrowUpCircle, ArrowDownCircle, Edit3, AlertCircle, RotateCcw } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import type { StockLevel } from '@/types/inventory';

interface AdjustStockModalProps {
  isOpen: boolean;
  onClose: () => void;
  stock: StockLevel | null;
  onConfirm: (
    movementType: 'IN' | 'OUT' | 'ADJUST' | 'DAMAGE' | 'RETURN',
    quantity: number,
    referenceNumber?: string,
    notes?: string
  ) => void;
  isLoading?: boolean;
}

export function AdjustStockModal({
  isOpen,
  onClose,
  stock,
  onConfirm,
  isLoading,
}: AdjustStockModalProps) {
  const [movementType, setMovementType] = useState<'IN' | 'OUT' | 'ADJUST' | 'DAMAGE' | 'RETURN'>('IN');
  const [quantity, setQuantity] = useState<number>(0);
  const [referenceNumber, setReferenceNumber] = useState('');
  const [notes, setNotes] = useState('');

  if (!isOpen || !stock) return null;

  const handleConfirm = () => {
    if (quantity <= 0) {
      alert('Ingresa una cantidad válida');
      return;
    }

    if (movementType === 'OUT' && quantity > stock.available_quantity) {
      alert('No hay suficiente stock disponible');
      return;
    }

    onConfirm(
      movementType,
      quantity,
      referenceNumber || undefined,
      notes || undefined
    );

    // Reset form
    setQuantity(0);
    setReferenceNumber('');
    setNotes('');
  };

  const getNewQuantity = () => {
    switch (movementType) {
      case 'IN':
      case 'RETURN':
        return stock.quantity + quantity;
      case 'OUT':
      case 'DAMAGE':
        return stock.quantity - quantity;
      case 'ADJUST':
        return quantity;
      default:
        return stock.quantity;
    }
  };

  const movementTypes = [
    {
      id: 'IN' as const,
      label: 'Entrada',
      icon: ArrowUpCircle,
      color: 'green',
      description: 'Recibir stock',
    },
    {
      id: 'OUT' as const,
      label: 'Salida',
      icon: ArrowDownCircle,
      color: 'red',
      description: 'Retirar stock',
    },
    {
      id: 'ADJUST' as const,
      label: 'Ajuste',
      icon: Edit3,
      color: 'blue',
      description: 'Ajustar a cantidad exacta',
    },
    {
      id: 'DAMAGE' as const,
      label: 'Daño',
      icon: AlertCircle,
      color: 'orange',
      description: 'Registrar daño',
    },
    {
      id: 'RETURN' as const,
      label: 'Devolución',
      icon: RotateCcw,
      color: 'indigo',
      description: 'Devolución de cliente',
    },
  ];

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <h2 className="text-2xl font-bold text-gray-900">Ajustar Stock</h2>
          <button
            onClick={onClose}
            className="text-quaternary hover:text-secondary transition-colors"
            disabled={isLoading}
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 space-y-6">
          {/* Product info */}
          <div className="bg-gray-50 rounded-lg p-4">
            <p className="text-sm text-secondary mb-1">Producto</p>
            <p className="text-lg font-semibold text-gray-900">{stock.product_name}</p>
            {stock.product_sku && (
              <p className="text-sm text-tertiary font-mono">SKU: {stock.product_sku}</p>
            )}
            <div className="mt-3 grid grid-cols-3 gap-4 text-sm">
              <div>
                <span className="text-secondary">Stock actual:</span>
                <span className="ml-2 font-bold text-gray-900">{stock.quantity}</span>
              </div>
              <div>
                <span className="text-secondary">Reservado:</span>
                <span className="ml-2 font-bold text-gray-900">{stock.reserved_quantity}</span>
              </div>
              <div>
                <span className="text-secondary">Disponible:</span>
                <span className="ml-2 font-bold text-green-600">{stock.available_quantity}</span>
              </div>
            </div>
          </div>

          {/* Movement type selection */}
          <div>
            <Label className="mb-3">Tipo de movimiento</Label>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
              {movementTypes.map((type) => {
                const Icon = type.icon;
                const isSelected = movementType === type.id;
                return (
                  <button
                    key={type.id}
                    onClick={() => setMovementType(type.id)}
                    className={`
                      p-3 rounded-lg border-2 transition-all text-left
                      ${
                        isSelected
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }
                    `}
                  >
                    <Icon
                      className={`w-5 h-5 mb-2 ${
                        isSelected ? 'text-blue-600' : 'text-quaternary'
                      }`}
                    />
                    <p
                      className={`text-sm font-medium ${
                        isSelected ? 'text-blue-900' : 'text-secondary'
                      }`}
                    >
                      {type.label}
                    </p>
                    <p className="text-xs text-tertiary mt-1">
                      {type.description}
                    </p>
                  </button>
                );
              })}
            </div>
          </div>

          {/* Quantity input */}
          <div>
            <Label htmlFor="quantity">
              {movementType === 'ADJUST' ? 'Nueva cantidad' : 'Cantidad'}
            </Label>
            <Input
              id="quantity"
              type="number"
              min="0"
              value={quantity || ''}
              onChange={(e) => setQuantity(parseInt(e.target.value) || 0)}
              placeholder="0"
            />
            {movementType === 'OUT' && quantity > stock.available_quantity && (
              <p className="text-sm text-red-600 mt-1">
                Cantidad excede el stock disponible ({stock.available_quantity})
              </p>
            )}
          </div>

          {/* Reference number */}
          <div>
            <Label htmlFor="reference">Número de referencia (Opcional)</Label>
            <Input
              id="reference"
              type="text"
              value={referenceNumber}
              onChange={(e) => setReferenceNumber(e.target.value)}
              placeholder="Ej: PO-12345, FACTURA-001"
            />
          </div>

          {/* Notes */}
          <div>
            <Label htmlFor="notes">Notas (Opcional)</Label>
            <textarea
              id="notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Motivo del ajuste, observaciones..."
              rows={3}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Preview */}
          {quantity > 0 && (
            <div className="bg-blue-50 rounded-lg p-4 border border-blue-200">
              <p className="text-sm text-blue-700 mb-2">Vista previa</p>
              <div className="flex items-center justify-between">
                <span className="text-sm text-blue-600">Stock actual:</span>
                <span className="font-semibold text-blue-900">{stock.quantity}</span>
              </div>
              {movementType !== 'ADJUST' && (
                <div className="flex items-center justify-between mt-1">
                  <span className="text-sm text-blue-600">
                    {['IN', 'RETURN'].includes(movementType) ? 'Agregar:' : 'Restar:'}
                  </span>
                  <span
                    className={`font-semibold ${
                      ['IN', 'RETURN'].includes(movementType)
                        ? 'text-green-600'
                        : 'text-red-600'
                    }`}
                  >
                    {['IN', 'RETURN'].includes(movementType) ? '+' : '-'}
                    {quantity}
                  </span>
                </div>
              )}
              <div className="flex items-center justify-between mt-2 pt-2 border-t border-blue-300">
                <span className="text-sm font-semibold text-blue-700">Nuevo stock:</span>
                <span className="text-xl font-bold text-blue-900">{getNewQuantity()}</span>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex gap-3 p-6 border-t bg-gray-50">
          <Button
            variant="outline"
            onClick={onClose}
            disabled={isLoading}
            className="flex-1"
          >
            Cancelar
          </Button>
          <Button
            onClick={handleConfirm}
            disabled={isLoading || quantity <= 0}
            className="flex-1"
          >
            {isLoading ? 'Procesando...' : 'Confirmar Ajuste'}
          </Button>
        </div>
      </div>
    </div>
  );
}

'use client';

import { ArrowUpCircle, ArrowDownCircle, Edit3, TrendingUp, AlertCircle, RotateCcw } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import type { StockMovement } from '@/types/inventory';

interface StockMovementsProps {
  movements: StockMovement[];
  isLoading?: boolean;
}

export function StockMovements({ movements, isLoading }: StockMovementsProps) {
  const getMovementConfig = (type: StockMovement['movement_type']) => {
    const configs = {
      IN: {
        label: 'Entrada',
        icon: ArrowUpCircle,
        variant: 'success' as const,
        color: 'text-green-600',
      },
      OUT: {
        label: 'Salida',
        icon: ArrowDownCircle,
        variant: 'destructive' as const,
        color: 'text-red-600',
      },
      ADJUST: {
        label: 'Ajuste',
        icon: Edit3,
        variant: 'default' as const,
        color: 'text-blue-600',
      },
      TRANSFER: {
        label: 'Transferencia',
        icon: TrendingUp,
        variant: 'secondary' as const,
        color: 'text-purple-600',
      },
      DAMAGE: {
        label: 'Daño',
        icon: AlertCircle,
        variant: 'warning' as const,
        color: 'text-orange-600',
      },
      RETURN: {
        label: 'Devolución',
        icon: RotateCcw,
        variant: 'secondary' as const,
        color: 'text-indigo-600',
      },
    };
    return configs[type] || configs.ADJUST;
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('es-CO', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    }).format(date);
  };

  if (isLoading) {
    return (
      <div className="animate-pulse space-y-4">
        <div className="h-20 bg-gray-200 rounded"></div>
        <div className="h-20 bg-gray-200 rounded"></div>
        <div className="h-20 bg-gray-200 rounded"></div>
      </div>
    );
  }

  if (movements.length === 0) {
    return (
      <div className="text-center py-12">
        <TrendingUp className="w-16 h-16 mx-auto text-gray-300 mb-4" />
        <p className="text-tertiary text-lg">No hay movimientos registrados</p>
        <p className="text-quaternary text-sm mt-2">
          Los movimientos de stock aparecerán aquí
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-3">
      {movements.map((movement) => {
        const config = getMovementConfig(movement.movement_type);
        const Icon = config.icon;

        return (
          <div
            key={movement.id}
            className="flex items-center gap-4 p-4 bg-white border border-gray-200 rounded-lg hover:shadow-md transition-shadow"
          >
            {/* Icon */}
            <div className={`flex-shrink-0 w-12 h-12 rounded-full bg-gray-100 flex items-center justify-center ${config.color}`}>
              <Icon className="w-6 h-6" />
            </div>

            {/* Info */}
            <div className="flex-1 min-w-0">
              <div className="flex items-center gap-2 mb-1">
                <h4 className="font-medium text-gray-900">
                  {movement.product_name || 'Producto'}
                </h4>
                <Badge variant={config.variant}>{config.label}</Badge>
              </div>
              <div className="text-sm text-secondary space-y-1">
                <div className="flex items-center gap-4">
                  <span>
                    <span className="font-medium">Almacén:</span> {movement.warehouse_name}
                    {movement.location_name && ` - ${movement.location_name}`}
                  </span>
                  {movement.reference_number && (
                    <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded">
                      Ref: {movement.reference_number}
                    </span>
                  )}
                </div>
                {movement.notes && (
                  <p className="text-tertiary italic">{movement.notes}</p>
                )}
              </div>
            </div>

            {/* Quantity and Date */}
            <div className="text-right">
              <div className={`text-2xl font-bold ${config.color}`}>
                {movement.movement_type === 'IN' || movement.movement_type === 'RETURN' ? '+' : '-'}
                {movement.quantity}
              </div>
              <div className="text-xs text-tertiary mt-1">
                {formatDate(movement.created_at)}
              </div>
              {movement.user_name && (
                <div className="text-xs text-quaternary mt-1">
                  Por: {movement.user_name}
                </div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
}

'use client';

import { Package, AlertTriangle, ArrowUpCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import type { StockLevel } from '@/types/inventory';

interface StockTableProps {
  stock: StockLevel[];
  onAdjust: (stock: StockLevel) => void;
  isLoading?: boolean;
}

export function StockTable({ stock, onAdjust, isLoading }: StockTableProps) {
  const getStockStatus = (item: StockLevel) => {
    if (item.quantity === 0) {
      return { label: 'Sin stock', variant: 'destructive' as const, icon: AlertTriangle };
    }
    if (item.reorder_point && item.quantity <= item.reorder_point) {
      return { label: 'Reabastecer', variant: 'warning' as const, icon: AlertTriangle };
    }
    if (item.min_stock_level && item.quantity <= item.min_stock_level) {
      return { label: 'Stock bajo', variant: 'warning' as const, icon: AlertTriangle };
    }
    return { label: 'Disponible', variant: 'success' as const, icon: Package };
  };

  if (isLoading) {
    return (
      <div className="animate-pulse space-y-4">
        <div className="h-12 bg-gray-200 rounded"></div>
        <div className="h-12 bg-gray-200 rounded"></div>
        <div className="h-12 bg-gray-200 rounded"></div>
      </div>
    );
  }

  if (stock.length === 0) {
    return (
      <div className="text-center py-12">
        <Package className="w-16 h-16 mx-auto text-gray-300 mb-4" />
        <p className="text-tertiary text-lg">No hay stock disponible</p>
        <p className="text-quaternary text-sm mt-2">
          Ajusta filtros o agrega productos al inventario
        </p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse">
        <thead>
          <tr className="border-b border-gray-200 bg-gray-50">
            <th className="text-left p-4 font-semibold text-secondary">Producto</th>
            <th className="text-left p-4 font-semibold text-secondary">Almacén</th>
            <th className="text-center p-4 font-semibold text-secondary">Cantidad</th>
            <th className="text-center p-4 font-semibold text-secondary">Reservado</th>
            <th className="text-center p-4 font-semibold text-secondary">Disponible</th>
            <th className="text-center p-4 font-semibold text-secondary">Estado</th>
            <th className="text-center p-4 font-semibold text-secondary">Acción</th>
          </tr>
        </thead>
        <tbody>
          {stock.map((item) => {
            const status = getStockStatus(item);
            const StatusIcon = status.icon;

            return (
              <tr
                key={item.id}
                className="border-b border-gray-100 hover:bg-gray-50 transition-colors"
              >
                <td className="p-4">
                  <div>
                    <div className="font-medium text-gray-900">
                      {item.product_name || 'Producto sin nombre'}
                    </div>
                    {item.product_sku && (
                      <div className="text-sm text-tertiary font-mono">
                        SKU: {item.product_sku}
                      </div>
                    )}
                  </div>
                </td>
                <td className="p-4">
                  <div>
                    <div className="text-gray-900">{item.warehouse_name}</div>
                    {item.location_name && (
                      <div className="text-sm text-tertiary">{item.location_name}</div>
                    )}
                  </div>
                </td>
                <td className="p-4 text-center">
                  <div className="font-semibold text-gray-900 text-lg">
                    {item.quantity}
                  </div>
                  {item.min_stock_level && (
                    <div className="text-xs text-tertiary">
                      Min: {item.min_stock_level}
                    </div>
                  )}
                </td>
                <td className="p-4 text-center">
                  <span className="text-secondary">{item.reserved_quantity}</span>
                </td>
                <td className="p-4 text-center">
                  <span className="font-semibold text-green-600">
                    {item.available_quantity}
                  </span>
                </td>
                <td className="p-4 text-center">
                  <Badge variant={status.variant} className="inline-flex items-center gap-1">
                    <StatusIcon className="w-3 h-3" />
                    {status.label}
                  </Badge>
                </td>
                <td className="p-4 text-center">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => onAdjust(item)}
                    title="Ajustar stock"
                  >
                    <ArrowUpCircle className="w-4 h-4 mr-1" />
                    Ajustar
                  </Button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

'use client';

import { Pencil, Trash2, Eye } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import type { Product } from '@/types/product';

interface ProductsTableProps {
  products: Product[];
  onEdit: (product: Product) => void;
  onDelete: (product: Product) => void;
  onView: (product: Product) => void;
  isLoading?: boolean;
}

export function ProductsTable({
  products,
  onEdit,
  onDelete,
  onView,
  isLoading,
}: ProductsTableProps) {
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const getStockStatus = (product: Product) => {
    if (product.stock_quantity === 0) {
      return <Badge variant="destructive">Sin stock</Badge>;
    }
    if (product.min_stock_level && product.stock_quantity <= product.min_stock_level) {
      return <Badge variant="warning">Stock bajo</Badge>;
    }
    return <Badge variant="success">En stock</Badge>;
  };

  if (isLoading) {
    return (
      <div className="animate-pulse">
        <div className="h-12 bg-gray-200 rounded mb-4"></div>
        <div className="h-12 bg-gray-200 rounded mb-4"></div>
        <div className="h-12 bg-gray-200 rounded mb-4"></div>
      </div>
    );
  }

  if (products.length === 0) {
    return (
      <div className="text-center py-12">
        <p className="text-tertiary text-lg">No se encontraron productos</p>
        <p className="text-quaternary text-sm mt-2">
          Intenta ajustar los filtros o crear un nuevo producto
        </p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse">
        <thead>
          <tr className="border-b border-gray-200 bg-gray-50">
            <th className="text-left p-4 font-semibold text-secondary">Producto</th>
            <th className="text-left p-4 font-semibold text-secondary">SKU</th>
            <th className="text-left p-4 font-semibold text-secondary">Categoría</th>
            <th className="text-right p-4 font-semibold text-secondary">Precio</th>
            <th className="text-center p-4 font-semibold text-secondary">Stock</th>
            <th className="text-center p-4 font-semibold text-secondary">Estado</th>
            <th className="text-center p-4 font-semibold text-secondary">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr
              key={product.id}
              className="border-b border-gray-100 hover:bg-gray-50 transition-colors"
            >
              <td className="p-4">
                <div className="flex items-center gap-3">
                  {product.image_url ? (
                    <img
                      src={product.image_url}
                      alt={product.name}
                      className="w-10 h-10 rounded object-cover"
                    />
                  ) : (
                    <div className="w-10 h-10 rounded bg-gray-200 flex items-center justify-center">
                      <span className="text-quaternary text-xs">IMG</span>
                    </div>
                  )}
                  <div>
                    <div className="font-medium text-gray-900">{product.name}</div>
                    {product.description && (
                      <div className="text-sm text-tertiary truncate max-w-xs">
                        {product.description}
                      </div>
                    )}
                  </div>
                </div>
              </td>
              <td className="p-4">
                <span className="font-mono text-sm text-secondary">{product.sku}</span>
              </td>
              <td className="p-4">
                <span className="text-sm text-secondary">
                  {product.category || '-'}
                </span>
              </td>
              <td className="p-4 text-right">
                <span className="font-semibold text-gray-900">
                  {formatCurrency(product.price)}
                </span>
              </td>
              <td className="p-4 text-center">
                <div>
                  <span className="font-semibold text-gray-900">
                    {product.stock_quantity}
                  </span>
                  {product.unit_of_measure && (
                    <span className="text-sm text-tertiary ml-1">
                      {product.unit_of_measure}
                    </span>
                  )}
                </div>
                {getStockStatus(product)}
              </td>
              <td className="p-4 text-center">
                {product.is_active ? (
                  <Badge variant="success">Activo</Badge>
                ) : (
                  <Badge variant="default">Inactivo</Badge>
                )}
              </td>
              <td className="p-4">
                <div className="flex items-center justify-center gap-2">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onView(product)}
                    title="Ver detalles"
                  >
                    <Eye className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(product)}
                    title="Editar"
                  >
                    <Pencil className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(product)}
                    title="Eliminar"
                    className="text-red-600 hover:text-red-700 hover:bg-red-50"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

