:root {
  /* Background Layers */
  --background: 0 0% 100%;
  --surface-1: 0 0% 100%;
  --surface-2: 240 4.8% 95.9%;
  --surface-3: 240 5.9% 90%;

  /* Text Hierarchy */
  --foreground: 240 10% 3.9%;
  --text-primary: 240 10% 3.9%;
  --text-secondary: 240 5.9% 25%;
  --text-tertiary: 240 3.8% 46.1%;
  --text-quaternary: 240 3% 65%;
  --text-disabled: 240 2% 75%;

  /* UI Elements */
  --card: 0 0% 100%;
  --card-foreground: 240 10% 3.9%;
  --popover: 0 0% 100%;
  --popover-foreground: 240 10% 3.9%;

  /* Brand Colors */
  --primary: 221 83% 53%;
  --primary-hover: 221 83% 48%;
  --primary-active: 221 83% 43%;
  --primary-foreground: 0 0% 98%;
  --primary-subtle: 221 83% 95%;
  --primary-muted: 221 50% 85%;

  --accent: 265 80% 65%;
  --accent-hover: 265 80% 60%;
  --accent-foreground: 0 0% 98%;
  --accent-subtle: 265 80% 95%;

  /* Semantic Colors */
  --success: 142 76% 36%;
  --success-hover: 142 76% 31%;
  --success-foreground: 0 0% 98%;
  --success-subtle: 142 76% 95%;

  --warning: 38 92% 50%;
  --warning-hover: 38 92% 45%;
  --warning-foreground: 240 10% 3.9%;
  --warning-subtle: 38 92% 95%;

  --error: 0 84% 60%;
  --error-hover: 0 84% 55%;
  --error-foreground: 0 0% 98%;
  --error-subtle: 0 84% 97%;

  --info: 199 89% 48%;
  --info-hover: 199 89% 43%;
  --info-foreground: 0 0% 98%;
  --info-subtle: 199 89% 95%;

  /* Legacy Compatibility */
  --secondary: 240 4.8% 95.9%;
  --secondary-foreground: 240 5.9% 10%;
  --muted: 240 4.8% 95.9%;
  --muted-foreground: 240 3.8% 46.1%;
  --destructive: 0 84.2% 60.2%;
  --destructive-foreground: 0 0% 98%;

  /* Borders */
  --border: 240 5.9% 90%;
  --border-strong: 240 5.9% 80%;
  --border-subtle: 240 5.9% 94%;
  --divider: 240 5.9% 90%;

  /* Interactive States */
  --hover-bg: 240 4.8% 95.9%;
  --active-bg: 240 5% 92%;
  --selected-bg: 221 83% 95%;
  --disabled-bg: 240 4.8% 97%;

  --input: 240 5.9% 90%;
  --ring: 221 83% 53%;
  --radius: 0.5rem;
}
