export function useResetSettings() {
  const qc = useQueryClient();
  return useMutation({
    mutationFn: () => settingsApi.resetToDefaults().then((r) => r.data),
    onSuccess: () => qc.invalidateQueries({ queryKey: KEY }),
  });
}

import { apiClient } from './client';
