  markAsRead: (id: number) =>
    apiClient.patch<Notification>(`${BASE}/${id}/read`),

  markAllAsRead: () =>
    apiClient.patch<{ updated: number }>(`${BASE}/read-all`),

  clearRead: () =>
    apiClient.delete<{ deleted: number }>(`${BASE}/clear-read`),

  remove: (id: number) =>
    apiClient.delete<{ message: string }>(`${BASE}/${id}`),

