  const { data: summary, isLoading: sumLoading } = useSalesSummary(query);
  const { data: periodData, isLoading: perLoading } = useSalesByPeriod(query);
  const { data: trends } = useRevenueTrends(query);

  const handleExport = (format: ExportFormat) => {
    window.open(reportsApi.exportSalesReport({ ...query, format }), '_blank');
  };

  if (sumLoading || perLoading) {
    return (
      <div className="space-y-6 animate-pulse">
        <div className="h-32 bg-gray-100 rounded" />
        <div className="grid grid-cols-4 gap-4">
          {[1, 2, 3, 4].map((i) => <div key={i} className="h-32 bg-gray-100 rounded" />)}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <ReportFilters onFilterChange={setQuery} onExport={handleExport} />

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {/* Total Ventas */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600">Total Ventas</CardTitle>
            <ShoppingCart className="h-4 w-4 text-gray-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{summary?.totalSales ?? 0}</div>
            {summary?.comparedToLastPeriod && (
              <p className="text-xs flex items-center gap-1 mt-1 text-gray-500">
                {summary.comparedToLastPeriod.salesChange >= 0
                  ? <TrendingUp className="h-3 w-3 text-green-600" />
                  : <TrendingDown className="h-3 w-3 text-red-600" />}
                <span className={summary.comparedToLastPeriod.salesChange >= 0 ? 'text-green-600' : 'text-red-600'}>
                  {formatPct(summary.comparedToLastPeriod.salesChange)}
                </span>
                vs período anterior
              </p>
            )}
          </CardContent>
        </Card>

        {/* Ingresos */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600">Ingresos Totales</CardTitle>
            <DollarSign className="h-4 w-4 text-gray-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{formatCurrency(summary?.totalRevenue ?? 0)}</div>
            {summary?.comparedToLastPeriod && (
              <p className="text-xs flex items-center gap-1 mt-1 text-gray-500">
                {summary.comparedToLastPeriod.revenueChange >= 0
                  ? <TrendingUp className="h-3 w-3 text-green-600" />
                  : <TrendingDown className="h-3 w-3 text-red-600" />}
                <span className={summary.comparedToLastPeriod.revenueChange >= 0 ? 'text-green-600' : 'text-red-600'}>
                  {formatPct(summary.comparedToLastPeriod.revenueChange)}
                </span>
                vs período anterior
              </p>
            )}
          </CardContent>
        </Card>

        {/* Ganancia */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600">Ganancia Total</CardTitle>
            <TrendingUp className="h-4 w-4 text-gray-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{formatCurrency(summary?.totalProfit ?? 0)}</div>
            {summary?.comparedToLastPeriod && (
              <p className="text-xs flex items-center gap-1 mt-1 text-gray-500">
                {summary.comparedToLastPeriod.profitChange >= 0
                  ? <TrendingUp className="h-3 w-3 text-green-600" />
                  : <TrendingDown className="h-3 w-3 text-red-600" />}
                <span className={summary.comparedToLastPeriod.profitChange >= 0 ? 'text-green-600' : 'text-red-600'}>
                  {formatPct(summary.comparedToLastPeriod.profitChange)}
                </span>
                vs período anterior
              </p>
            )}
          </CardContent>
        </Card>

        {/* Ticket Promedio */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600">Ticket Promedio</CardTitle>
            <Package className="h-4 w-4 text-gray-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{formatCurrency(summary?.averageTicket ?? 0)}</div>
            <p className="text-xs text-gray-500 mt-1">{summary?.totalItems ?? 0} artículos vendidos</p>
          </CardContent>
        </Card>
      </div>

      {/* Métodos de Pago */}
      {trends && trends.byPaymentMethod.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Ingresos por Método de Pago</CardTitle>
            <CardDescription>Distribución de ingresos según forma de pago</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {trends.byPaymentMethod.map((method) => (
                <div key={method.paymentMethod} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div className="flex items-center gap-3">
                    <CreditCard className="h-5 w-5 text-gray-500" />
                    <div>
                      <p className="font-medium capitalize">{method.paymentMethod}</p>
                      <p className="text-sm text-gray-500">{method.transactionCount} transacciones</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="font-bold">{formatCurrency(method.totalRevenue)}</p>
                    <p className="text-sm text-gray-500">{method.percentage.toFixed(1)}%</p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Tabla de Ventas por Período */}
      {periodData && periodData.periodData.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Ventas por Período</CardTitle>
            <CardDescription>Desglose detallado de ventas en el período seleccionado</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="overflow-x-auto">
              <table className="w-full text-sm">
                <thead>
                  <tr className="border-b text-gray-500 text-xs uppercase">
                    <th className="text-left py-3 px-4">Fecha</th>
                    <th className="text-right py-3 px-4">Ventas</th>
                    <th className="text-right py-3 px-4">Ingresos</th>
                    <th className="text-right py-3 px-4">Artículos</th>
                    <th className="text-right py-3 px-4">Ticket Prom.</th>
                  </tr>
                </thead>
                <tbody>
                  {periodData.periodData.map((row, i) => (
                    <tr key={i} className="border-b hover:bg-gray-50">
                      <td className="py-3 px-4 font-medium">{row.date}</td>
                      <td className="py-3 px-4 text-right">{row.totalSales}</td>
                      <td className="py-3 px-4 text-right">{formatCurrency(row.totalRevenue)}</td>
                      <td className="py-3 px-4 text-right">{row.totalItems}</td>
                      <td className="py-3 px-4 text-right">{formatCurrency(row.averageTicket)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { ReportFilters } from './ReportFilters';
import { useTopSellingProducts, useLowStockProducts } from '@/hooks/useReports';
import { reportsApi } from '@/lib/api/reports';
import { AlertTriangle, TrendingUp } from 'lucide-react';
import type { ReportQuery, ExportFormat } from '@/types/reports';

const formatCurrency = (value: number) =>
  new Intl.NumberFormat('es-CO', { style: 'currency', currency: 'COP', minimumFractionDigits: 0 }).format(value);

export function ProductReportTab() {
  const [query, setQuery] = useState<ReportQuery>({ period: 'monthly', limit: 10 });

  const { data: topSelling, isLoading: topLoading } = useTopSellingProducts(query);
  const { data: lowStock, isLoading: lowLoading } = useLowStockProducts(query);

  const handleExport = (format: ExportFormat) => {
    window.open(reportsApi.exportProductReport({ ...query, format }), '_blank');
  };

  if (topLoading || lowLoading) {
    return (
      <div className="space-y-6 animate-pulse">
        <div className="h-32 bg-gray-100 rounded" />
        <div className="h-64 bg-gray-100 rounded" />
        <div className="h-64 bg-gray-100 rounded" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <ReportFilters onFilterChange={setQuery} onExport={handleExport} />

      {/* Top Selling Products */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5" />
            Productos Más Vendidos
          </CardTitle>
          <CardDescription>Los {topSelling?.length ?? 0} productos con mejor desempeño en el período</CardDescription>
        </CardHeader>
        <CardContent>
          {topSelling && topSelling.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="w-full text-sm">
                <thead>
                  <tr className="border-b text-gray-500 text-xs uppercase">
                    <th className="text-left py-3 px-4">#</th>
                    <th className="text-left py-3 px-4">Producto</th>
                    <th className="text-left py-3 px-4">Categoría</th>
                    <th className="text-right py-3 px-4">Cantidad</th>
                    <th className="text-right py-3 px-4">Ingresos</th>
                    <th className="text-right py-3 px-4">Precio Prom.</th>
                  </tr>
                </thead>
                <tbody>
                  {topSelling.map((product, i) => (
                    <tr key={product.productId} className="border-b hover:bg-gray-50">
                      <td className="py-3 px-4 text-gray-400 font-medium">{i + 1}</td>
                      <td className="py-3 px-4">
                        <div>
                          <p className="font-medium">{product.productName}</p>
                          <p className="text-xs text-gray-400">{product.sku}</p>
                        </div>
                      </td>
                      <td className="py-3 px-4 text-gray-600">{product.category}</td>
                      <td className="py-3 px-4 text-right font-medium">{product.totalQuantitySold}</td>
                      <td className="py-3 px-4 text-right">{formatCurrency(product.totalRevenue)}</td>
                      <td className="py-3 px-4 text-right">{formatCurrency(product.averagePrice)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-center text-gray-500 py-8">No hay datos de ventas disponibles</p>
          )}
        </CardContent>
      </Card>

      {/* Low Stock Alert */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <AlertTriangle className="h-5 w-5 text-orange-500" />
            Alertas de Stock Bajo
          </CardTitle>
          <CardDescription>Productos que necesitan reabastecimiento urgente</CardDescription>
        </CardHeader>
        <CardContent>
          {lowStock && lowStock.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="w-full text-sm">
                <thead>
                  <tr className="border-b text-gray-500 text-xs uppercase">
                    <th className="text-left py-3 px-4">Producto</th>
                    <th className="text-left py-3 px-4">Almacén</th>
                    <th className="text-right py-3 px-4">Stock Actual</th>
                    <th className="text-right py-3 px-4">Punto Reorden</th>
                    <th className="text-center py-3 px-4">Estado</th>
                    <th className="text-right py-3 px-4">Días Restantes</th>
                  </tr>
                </thead>
                <tbody>
                  {lowStock.map((product) => (
                    <tr key={`${product.productId}-${product.warehouseName}`} className="border-b hover:bg-gray-50">
                      <td className="py-3 px-4">
                        <div>
                          <p className="font-medium">{product.productName}</p>
                          <p className="text-xs text-gray-400">{product.sku}</p>
                        </div>
                      </td>
                      <td className="py-3 px-4 text-gray-600">{product.warehouseName}</td>
                      <td className="py-3 px-4 text-right font-bold text-red-600">{product.currentStock}</td>
                      <td className="py-3 px-4 text-right text-gray-600">{product.reorderPoint}</td>
                      <td className="py-3 px-4 text-center">
                        <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                          product.stockLevel === 'critical'
                            ? 'bg-red-100 text-red-700'
                            : 'bg-orange-100 text-orange-700'
                        }`}>
                          {product.stockLevel === 'critical' ? '⚠️ Crítico' : '⚠️ Bajo'}
                        </span>
                      </td>
                      <td className="py-3 px-4 text-right text-gray-600">
                        {product.daysUntilStockout !== undefined ? `${product.daysUntilStockout} días` : 'N/A'}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-center text-gray-500 py-8">
              ✅ Todos los productos tienen stock suficiente
            </p>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Download, RefreshCw } from 'lucide-react';
import type { PeriodType, ExportFormat, ReportQuery } from '@/types/reports';

interface ReportFiltersProps {
  onFilterChange: (query: ReportQuery) => void;
  onExport?: (format: ExportFormat) => void;
  showExport?: boolean;
}

export function ReportFilters({ onFilterChange, onExport, showExport = true }: ReportFiltersProps) {
  const [period, setPeriod] = useState<PeriodType>('monthly');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [limit, setLimit] = useState('10');

  const handleApply = () => {
    const query: ReportQuery = {
      period,
      ...(period === 'custom' && startDate && endDate ? { startDate, endDate } : {}),
      limit: parseInt(limit) || 10,
    };
    onFilterChange(query);
  };

  const handleReset = () => {
    setPeriod('monthly');
    setStartDate('');
    setEndDate('');
    setLimit('10');
    onFilterChange({ period: 'monthly', limit: 10 });
  };

  return (
    <Card>
      <CardContent className="pt-6">
        <div className="grid grid-cols-1 md:grid-cols-5 gap-4 items-end">
          {/* Period */}
          <div className="space-y-2">
            <Label htmlFor="period">Período</Label>
            <select
              id="period"
              value={period}
              onChange={(e) => setPeriod(e.target.value as PeriodType)}
              className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2"
            >
              <option value="daily">Hoy</option>
              <option value="weekly">Última Semana</option>
              <option value="monthly">Último Mes</option>
              <option value="yearly">Último Año</option>
              <option value="custom">Personalizado</option>
            </select>
          </div>

          {/* Custom dates */}
          {period === 'custom' ? (
            <>
              <div className="space-y-2">
                <Label htmlFor="startDate">Fecha Inicio</Label>
                <Input id="startDate" type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
              </div>
              <div className="space-y-2">
                <Label htmlFor="endDate">Fecha Fin</Label>
                <Input id="endDate" type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
              </div>
            </>
          ) : (
            <div className="space-y-2">
              <Label htmlFor="limit">Límite resultados</Label>
              <Input
                id="limit"
                type="number"
                min="1"
                max="100"
                value={limit}
                onChange={(e) => setLimit(e.target.value)}
              />
            </div>
          )}

          {/* Buttons */}
          <div className="flex gap-2 md:col-span-2">
            <Button onClick={handleApply} className="flex-1">
              <RefreshCw className="h-4 w-4 mr-2" />
              Aplicar
            </Button>
            <Button variant="outline" onClick={handleReset}>
              Limpiar
            </Button>
          </div>
        </div>

        {/* Export buttons */}
        {showExport && onExport && (
          <div className="mt-4 flex gap-2 flex-wrap">
            <Button variant="outline" size="sm" onClick={() => onExport('csv')}>
              <Download className="h-4 w-4 mr-2" />
              Exportar CSV
            </Button>
            <Button variant="outline" size="sm" disabled title="Próximamente">
              <Download className="h-4 w-4 mr-2" />
              Exportar Excel
            </Button>
            <Button variant="outline" size="sm" disabled title="Próximamente">
              <Download className="h-4 w-4 mr-2" />
              Exportar PDF
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

'use client';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import type { UserResponse } from '@/types/users';
import { Pencil, Shield, KeyRound, Trash2, ToggleLeft, ToggleRight } from 'lucide-react';

const ROLE_COLORS: Record<string, string> = {
  admin: 'bg-red-100 text-red-700',
  manager: 'bg-purple-100 text-purple-700',
  staff: 'bg-blue-100 text-blue-700',
  cashier: 'bg-green-100 text-green-700',
};

interface UsersTableProps {
  users: UserResponse[];
  isLoading: boolean;
  onEdit: (user: UserResponse) => void;
  onAssignRoles: (user: UserResponse) => void;
  onResetPassword: (user: UserResponse) => void;
  onToggleStatus: (user: UserResponse) => void;
  onDelete: (user: UserResponse) => void;
}

export function UsersTable({
  users,
  isLoading,
  onEdit,
  onAssignRoles,
  onResetPassword,
  onToggleStatus,
  onDelete,
}: UsersTableProps) {
  if (isLoading) {
    return (
      <div className="space-y-3">
        {[1, 2, 3, 4, 5].map((i) => (
          <div key={i} className="h-16 bg-gray-100 animate-pulse rounded" />
        ))}
      </div>
    );
  }

  if (users.length === 0) {
    return (
      <div className="text-center py-12 text-gray-500">
        <p className="text-lg font-medium">No se encontraron usuarios</p>
        <p className="text-sm mt-1">Intenta cambiar los filtros o crear un nuevo usuario</p>
      </div>
    );
  }

  const formatDate = (dateStr?: string) => {
    if (!dateStr) return 'Nunca';
    return new Date(dateStr).toLocaleDateString('es-CO', {
      day: '2-digit',
      month: 'short',
      year: 'numeric',
    });
  };

  return (
    <div className="overflow-x-auto">
      <table className="w-full text-sm">
        <thead>
          <tr className="border-b text-gray-500 text-xs uppercase">
            <th className="text-left py-3 px-4">Usuario</th>
            <th className="text-left py-3 px-4">Email</th>
            <th className="text-left py-3 px-4">Roles</th>
            <th className="text-center py-3 px-4">Estado</th>
            <th className="text-left py-3 px-4">Último acceso</th>
            <th className="text-left py-3 px-4">Registrado</th>
            <th className="text-right py-3 px-4">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id} className="border-b hover:bg-gray-50">
              <td className="py-3 px-4">
                <div>
                  <p className="font-medium">{user.name}</p>
                  {user.phone && <p className="text-xs text-gray-400">{user.phone}</p>}
                </div>
              </td>
              <td className="py-3 px-4 text-gray-600">{user.email}</td>
              <td className="py-3 px-4">
                <div className="flex flex-wrap gap-1">
                  {user.roles.length === 0 ? (
                    <span className="text-gray-400 text-xs">Sin roles</span>
                  ) : (
                    user.roles.map((role) => (
                      <span
                        key={role.id}
                        className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium ${
                          ROLE_COLORS[role.name.toLowerCase()] || 'bg-gray-100 text-gray-700'
                        }`}
                      >
                        {role.name}
                      </span>
                    ))
                  )}
                </div>
              </td>
              <td className="py-3 px-4 text-center">
                <span
                  className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                    user.isActive ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-500'
                  }`}
                >
                  {user.isActive ? 'Activo' : 'Inactivo'}
                </span>
              </td>
              <td className="py-3 px-4 text-gray-500">{formatDate(user.lastLogin)}</td>
              <td className="py-3 px-4 text-gray-500">{formatDate(user.createdAt)}</td>
              <td className="py-3 px-4">
                <div className="flex items-center justify-end gap-1">
                  <Button variant="ghost" size="sm" onClick={() => onEdit(user)} title="Editar">
                    <Pencil className="h-4 w-4" />
                  </Button>
                  <Button variant="ghost" size="sm" onClick={() => onAssignRoles(user)} title="Asignar roles">
                    <Shield className="h-4 w-4" />
                  </Button>
                  <Button variant="ghost" size="sm" onClick={() => onResetPassword(user)} title="Resetear contraseña">
                    <KeyRound className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onToggleStatus(user)}
                    title={user.isActive ? 'Desactivar' : 'Activar'}
                  >
                    {user.isActive
                      ? <ToggleRight className="h-4 w-4 text-green-600" />
                      : <ToggleLeft className="h-4 w-4 text-gray-400" />}
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(user)}
                    title="Eliminar"
                    className="text-red-500 hover:text-red-700"
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { useRoles } from '@/hooks/useUsers';
import type { UserResponse, CreateUserDto, UpdateUserDto, Role } from '@/types/users';
import { X } from 'lucide-react';

interface UserFormProps {
  user?: UserResponse | null;
  onSubmit: (dto: CreateUserDto | UpdateUserDto) => void;
  onCancel: () => void;
  isLoading?: boolean;
}

export function UserForm({ user, onSubmit, onCancel, isLoading }: UserFormProps) {
  const isEdit = !!user;
  const { data: roles = [] } = useRoles();

  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    phone: '',
    isActive: true,
    roleIds: [] as string[],
  });

  const [errors, setErrors] = useState<Record<string, string>>({});

  useEffect(() => {
    if (user) {
      setForm({
        name: user.name,
        email: user.email,
        password: '',
        firstName: user.firstName || '',
        lastName: user.lastName || '',
        phone: user.phone || '',
        isActive: user.isActive,
        roleIds: user.roles.map((r) => r.id),
      });
    }
  }, [user]);

  const validate = () => {
    const errs: Record<string, string> = {};
    if (!form.name.trim()) errs.name = 'El nombre es requerido';
    if (!isEdit && !form.email.trim()) errs.email = 'El email es requerido';
    if (!isEdit && !form.password) errs.password = 'La contraseña es requerida';
    if (!isEdit && form.password.length < 8) errs.password = 'Mínimo 8 caracteres';
    setErrors(errs);
    return Object.keys(errs).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!validate()) return;

    if (isEdit) {
      const dto: UpdateUserDto = {
        name: form.name,
        firstName: form.firstName || undefined,
        lastName: form.lastName || undefined,
        phone: form.phone || undefined,
        isActive: form.isActive,
      };
      onSubmit(dto);
    } else {
      const dto: CreateUserDto = {
        name: form.name,
        email: form.email,
        password: form.password,
        firstName: form.firstName || undefined,
        lastName: form.lastName || undefined,
        phone: form.phone || undefined,
        isActive: form.isActive,
        roleIds: form.roleIds,
      };
      onSubmit(dto);
    }
  };

  const toggleRole = (roleId: string) => {
    setForm((prev) => ({
      ...prev,
      roleIds: prev.roleIds.includes(roleId)
        ? prev.roleIds.filter((id) => id !== roleId)
        : [...prev.roleIds, roleId],
    }));
  };

  const field = (id: string, label: string, type = 'text', required = false) => (
    <div className="space-y-1">
      <Label htmlFor={id}>{label}{required && <span className="text-red-500 ml-1">*</span>}</Label>
      <Input
        id={id}
        type={type}
        value={(form as any)[id]}
        onChange={(e) => setForm((prev) => ({ ...prev, [id]: e.target.value }))}
        className={errors[id] ? 'border-red-500' : ''}
      />
      {errors[id] && <p className="text-xs text-red-500">{errors[id]}</p>}
    </div>
  );

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50">
      <Card className="w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle>{isEdit ? 'Editar Usuario' : 'Nuevo Usuario'}</CardTitle>
          <Button variant="ghost" size="sm" onClick={onCancel}>
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {field('name', 'Nombre completo', 'text', true)}
              {!isEdit && field('email', 'Email', 'email', true)}
              {!isEdit && field('password', 'Contraseña', 'password', true)}
              {field('firstName', 'Primer nombre')}
              {field('lastName', 'Apellido')}
              {field('phone', 'Teléfono')}
            </div>

            {/* Status */}
            <div className="flex items-center gap-3">
              <input
                type="checkbox"
                id="isActive"
                checked={form.isActive}
                onChange={(e) => setForm((prev) => ({ ...prev, isActive: e.target.checked }))}
                className="h-4 w-4"
              />
              <Label htmlFor="isActive">Usuario activo</Label>
            </div>

            {/* Roles (only for create) */}
            {!isEdit && roles.length > 0 && (
              <div className="space-y-2">
                <Label>Roles asignados</Label>
                <div className="grid grid-cols-2 gap-2">
                  {roles.map((role: Role) => (
                    <label
                      key={role.id}
                      className={`flex items-center gap-2 p-3 border rounded-lg cursor-pointer transition-colors ${
                        form.roleIds.includes(role.id)
                          ? 'bg-blue-50 border-blue-300'
                          : 'bg-white hover:bg-gray-50'
                      }`}
                    >
                      <input
                        type="checkbox"
                        checked={form.roleIds.includes(role.id)}
                        onChange={() => toggleRole(role.id)}
                        className="h-4 w-4"
                      />
                      <div>
                        <p className="text-sm font-medium">{role.name}</p>
                        <p className="text-xs text-gray-500">{role.description}</p>
                      </div>
                    </label>
                  ))}
                </div>
              </div>
            )}

            <div className="flex justify-end gap-3 pt-4 border-t">
              <Button type="button" variant="outline" onClick={onCancel}>
                Cancelar
              </Button>
              <Button type="submit" disabled={isLoading}>
                {isLoading ? 'Guardando...' : isEdit ? 'Guardar cambios' : 'Crear usuario'}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

