'use client';

import { useState } from 'react';
import { ShoppingCart, Receipt, TrendingUp, DollarSign } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { SalesCart } from '@/components/sales/SalesCart';
import { ProductSearch } from '@/components/sales/ProductSearch';
import { CustomerSelect } from '@/components/sales/CustomerSelect';
import { PaymentModal } from '@/components/sales/PaymentModal';
import { useCreateSale, useSalesStats } from '@/hooks/useSales';
import type { Product } from '@/types/product';
import type { CartItem, Cart } from '@/types/sale';

export default function SalesPage() {
  const [cart, setCart] = useState<Cart>({
    items: [],
    subtotal: 0,
    tax: 0,
    discount: 0,
    total: 0,
  });
  const [showPaymentModal, setShowPaymentModal] = useState(false);

  const { data: stats } = useSalesStats();
  const createSaleMutation = useCreateSale();

  const TAX_RATE = 0.19; // 19% IVA

  const calculateTotals = (items: CartItem[]) => {
    const subtotal = items.reduce((sum, item) => sum + item.subtotal, 0);
    const tax = subtotal * TAX_RATE;
    const total = subtotal + tax - cart.discount;

    return { subtotal, tax, total };
  };

  const handleAddProduct = (product: Product) => {
    if (product.stock_quantity === 0) {
      alert('Producto sin stock');
      return;
    }

    const existingItem = cart.items.find(
      (item) => item.product_id === product.id
    );

    let newItems: CartItem[];

    if (existingItem) {
      if (existingItem.quantity >= product.stock_quantity) {
        alert('No hay suficiente stock');
        return;
      }
      newItems = cart.items.map((item) =>
        item.product_id === product.id
          ? {
              ...item,
              quantity: item.quantity + 1,
              subtotal: (item.quantity + 1) * item.unit_price,
            }
          : item
      );
    } else {
      const newItem: CartItem = {
        product_id: product.id,
        product_name: product.name,
        quantity: 1,
        unit_price: product.price,
        tax_rate: product.tax_rate || TAX_RATE * 100,
        subtotal: product.price,
        stock_quantity: product.stock_quantity,
        image_url: product.image_url,
      };
      newItems = [...cart.items, newItem];
    }

    const totals = calculateTotals(newItems);
    setCart({ ...cart, items: newItems, ...totals });
  };

  const handleUpdateQuantity = (productId: string, quantity: number) => {
    if (quantity < 1) return;

    const newItems = cart.items.map((item) =>
      item.product_id === productId
        ? {
            ...item,
            quantity,
            subtotal: quantity * item.unit_price,
          }
        : item
    );

    const totals = calculateTotals(newItems);
    setCart({ ...cart, items: newItems, ...totals });
  };

  const handleRemoveItem = (productId: string) => {
    const newItems = cart.items.filter((item) => item.product_id !== productId);
    const totals = calculateTotals(newItems);
    setCart({ ...cart, items: newItems, ...totals });
  };

  const handleSelectCustomer = (
    customer: { id: string; name: string } | undefined
  ) => {
    setCart({
      ...cart,
      customer_id: customer?.id,
      customer_name: customer?.name,
    });
  };

  const handleCheckout = () => {
    if (cart.items.length === 0) {
      alert('El carrito está vacío');
      return;
    }
    setShowPaymentModal(true);
  };

  const handleConfirmPayment = async (
    paymentMethod: string,
    notes?: string
  ) => {
    try {
      const saleData = {
        customer_id: cart.customer_id,
        items: cart.items.map((item) => ({
          product_id: item.product_id,
          quantity: item.quantity,
          unit_price: item.unit_price,
          discount: item.discount || 0,
          tax_rate: item.tax_rate || TAX_RATE * 100,
        })),
        payment_method: paymentMethod,
        payment_status: 'paid' as const,
        discount_amount: cart.discount,
        notes,
      };

      await createSaleMutation.mutateAsync(saleData);

      alert('Venta completada exitosamente');
      setShowPaymentModal(false);
      // Reset cart
      setCart({
        items: [],
        subtotal: 0,
        tax: 0,
        discount: 0,
        total: 0,
      });
    } catch (error) {
      alert('Error al procesar la venta');
      console.error(error);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Punto de Venta</h1>
        <p className="text-gray-500 mt-1">Gestiona tus ventas y checkout</p>
      </div>

      {/* Stats Cards */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Ventas Hoy</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.todaySales}
                </p>
              </div>
              <ShoppingCart className="w-10 h-10 text-blue-500" />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Ingresos Hoy</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {formatCurrency(stats.todayRevenue)}
                </p>
              </div>
              <DollarSign className="w-10 h-10 text-green-500" />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Total Ventas</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalSales}
                </p>
              </div>
              <Receipt className="w-10 h-10 text-purple-500" />
            </div>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Ticket Promedio</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {formatCurrency(stats.averageOrderValue)}
                </p>
              </div>
              <TrendingUp className="w-10 h-10 text-orange-500" />
            </div>
          </Card>
        </div>
      )}

      {/* Main Content */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Left side - Product search and customer */}
        <div className="lg:col-span-2 space-y-6">
          {/* Product search */}
          <Card className="p-6">
            <h2 className="text-lg font-semibold mb-4">Buscar Productos</h2>
            <ProductSearch onAddProduct={handleAddProduct} />
          </Card>

          {/* Customer selection */}
          <CustomerSelect
            selectedCustomer={
              cart.customer_id && cart.customer_name
                ? { id: cart.customer_id, name: cart.customer_name }
                : undefined
            }
            onSelect={handleSelectCustomer}
          />
        </div>

        {/* Right side - Cart */}
        <div className="space-y-4">
          <div className="lg:sticky lg:top-6">
            <h2 className="text-lg font-semibold mb-4 flex items-center gap-2">
              <ShoppingCart className="w-5 h-5" />
              Carrito ({cart.items.length})
            </h2>

            <div className="space-y-4">
              <SalesCart
                items={cart.items}
                onUpdateQuantity={handleUpdateQuantity}
                onRemoveItem={handleRemoveItem}
                subtotal={cart.subtotal}
                tax={cart.tax}
                discount={cart.discount}
                total={cart.total}
              />

              <Button
                onClick={handleCheckout}
                disabled={cart.items.length === 0}
                className="w-full"
                size="lg"
              >
                Procesar Venta
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Payment Modal */}
      <PaymentModal
        isOpen={showPaymentModal}
        onClose={() => setShowPaymentModal(false)}
        total={cart.total}
        onConfirm={handleConfirmPayment}
        isLoading={createSaleMutation.isPending}
      />
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Package, TrendingUp, AlertTriangle, Warehouse as WarehouseIcon } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { StockTable } from '@/components/inventory/StockTable';
import { StockMovements } from '@/components/inventory/StockMovements';
import { AdjustStockModal } from '@/components/inventory/AdjustStockModal';
import { InventoryFilters } from '@/components/inventory/InventoryFilters';
import {
  useStock,
  useMovements,
  useWarehouses,
  useInventoryStats,
  useAdjustStock,
} from '@/hooks/useInventory';
import type { StockLevel, StockQueryParams, MovementQueryParams } from '@/types/inventory';

export default function InventoryPage() {
  const [stockParams, setStockParams] = useState<StockQueryParams>({
    page: 1,
    pageSize: 20,
  });
  const [movementParams, setMovementParams] = useState<MovementQueryParams>({
    page: 1,
    pageSize: 10,
  });
  const [adjustingStock, setAdjustingStock] = useState<StockLevel | null>(null);
  const [activeTab, setActiveTab] = useState<'stock' | 'movements'>('stock');

  // Queries
  const { data: stockData, isLoading: stockLoading } = useStock(stockParams);
  const { data: movementsData, isLoading: movementsLoading } = useMovements(movementParams);
  const { data: warehouses } = useWarehouses();
  const { data: stats } = useInventoryStats();

  // Mutations
  const adjustStockMutation = useAdjustStock();

  const handleFilterChange = (filters: StockQueryParams) => {
    setStockParams({ ...filters, page: 1, pageSize: 20 });
  };

  const handleAdjustStock = (stock: StockLevel) => {
    setAdjustingStock(stock);
  };

  const handleConfirmAdjust = async (
    movementType: 'IN' | 'OUT' | 'ADJUST' | 'DAMAGE' | 'RETURN',
    quantity: number,
    referenceNumber?: string,
    notes?: string
  ) => {
    if (!adjustingStock) return;

    try {
      await adjustStockMutation.mutateAsync({
        product_id: adjustingStock.product_id,
        warehouse_id: adjustingStock.warehouse_id,
        location_id: adjustingStock.location_id,
        movement_type: movementType,
        quantity,
        reference_number: referenceNumber,
        notes,
      });

      alert('Stock ajustado exitosamente');
      setAdjustingStock(null);
    } catch (error) {
      alert('Error al ajustar el stock');
      console.error(error);
    }
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Inventario</h1>
        <p className="text-gray-500 mt-1">Gestiona tu stock y movimientos de inventario</p>
      </div>

      {/* Stats Cards */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Total Productos</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalProducts}
                </p>
              </div>
              <Package className="w-10 h-10 text-blue-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              En inventario
            </p>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Stock Total</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalStock.toLocaleString()}
                </p>
              </div>
              <TrendingUp className="w-10 h-10 text-green-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Unidades en stock
            </p>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Stock Bajo</p>
                <p className="text-2xl font-bold text-yellow-600 mt-1">
                  {stats.lowStockItems}
                </p>
              </div>
              <AlertTriangle className="w-10 h-10 text-yellow-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Requieren reabastecimiento
            </p>
          </Card>

          <Card className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-500">Almacenes</p>
                <p className="text-2xl font-bold text-gray-900 mt-1">
                  {stats.totalWarehouses}
                </p>
              </div>
              <WarehouseIcon className="w-10 h-10 text-purple-500" />
            </div>
            <p className="text-sm text-gray-500 mt-4">
              Activos
            </p>
          </Card>
        </div>
      )}

      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="flex gap-8">
          <button
            onClick={() => setActiveTab('stock')}
            className={`
              pb-4 border-b-2 font-medium transition-colors
              ${
                activeTab === 'stock'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700'
              }
            `}
          >
            Stock Actual
          </button>
          <button
            onClick={() => setActiveTab('movements')}
            className={`
              pb-4 border-b-2 font-medium transition-colors
              ${
                activeTab === 'movements'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700'
              }
            `}
          >
            Movimientos
            {movementsData && movementsData.total > 0 && (
              <Badge variant="default" className="ml-2">
                {movementsData.total}
              </Badge>
            )}
          </button>
        </nav>
      </div>

      {/* Content */}
      {activeTab === 'stock' && (
        <>
          {/* Filters */}
          <InventoryFilters
            onFilterChange={handleFilterChange}
            warehouses={warehouses || []}
          />

          {/* Stock Table */}
          <Card className="overflow-hidden">
            <StockTable
              stock={stockData?.data || []}
              onAdjust={handleAdjustStock}
              isLoading={stockLoading}
            />

            {/* Pagination */}
            {stockData && stockData.total > 0 && (
              <div className="px-6 py-4 border-t border-gray-200 flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  Mostrando {((stockData.page - 1) * stockData.pageSize) + 1} -{' '}
                  {Math.min(stockData.page * stockData.pageSize, stockData.total)}{' '}
                  de {stockData.total} productos
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() =>
                      setStockParams((prev) => ({ ...prev, page: prev.page! - 1 }))
                    }
                    disabled={stockData.page === 1}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Anterior
                  </button>
                  <button
                    onClick={() =>
                      setStockParams((prev) => ({ ...prev, page: prev.page! + 1 }))
                    }
                    disabled={
                      stockData.page >= Math.ceil(stockData.total / stockData.pageSize)
                    }
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Siguiente
                  </button>
                </div>
              </div>
            )}
          </Card>
        </>
      )}

      {activeTab === 'movements' && (
        <>
          {/* Movements List */}
          <Card className="p-6">
            <StockMovements
              movements={movementsData?.data || []}
              isLoading={movementsLoading}
            />

            {/* Pagination */}
            {movementsData && movementsData.total > 0 && (
              <div className="mt-6 pt-6 border-t border-gray-200 flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  Mostrando {((movementsData.page - 1) * movementsData.pageSize) + 1} -{' '}
                  {Math.min(
                    movementsData.page * movementsData.pageSize,
                    movementsData.total
                  )}{' '}
                  de {movementsData.total} movimientos
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() =>
                      setMovementParams((prev) => ({ ...prev, page: prev.page! - 1 }))
                    }
                    disabled={movementsData.page === 1}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Anterior
                  </button>
                  <button
                    onClick={() =>
                      setMovementParams((prev) => ({ ...prev, page: prev.page! + 1 }))
                    }
                    disabled={
                      movementsData.page >=
                      Math.ceil(movementsData.total / movementsData.pageSize)
                    }
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Siguiente
                  </button>
                </div>
              </div>
            )}
          </Card>
        </>
      )}

      {/* Adjust Stock Modal */}
      <AdjustStockModal
        isOpen={!!adjustingStock}
        onClose={() => setAdjustingStock(null)}
        stock={adjustingStock}
        onConfirm={handleConfirmAdjust}
        isLoading={adjustStockMutation.isPending}
      />
    </div>
  );
}

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { salesApi } from '@/lib/api/sales';
import type { CreateSaleDto, UpdateSaleDto, SaleQueryParams } from '@/types/sale';

export const SALES_QUERY_KEY = 'sales';

/**
 * Hook to fetch all sales with pagination and filters
 */
export function useSales(params?: SaleQueryParams) {
  return useQuery({
    queryKey: [SALES_QUERY_KEY, params],
    queryFn: () => salesApi.getAll(params),
  });
}

/**
 * Hook to fetch a single sale by ID
 */
export function useSale(id: string) {
  return useQuery({
    queryKey: [SALES_QUERY_KEY, id],
    queryFn: () => salesApi.getById(id),
    enabled: !!id,
  });
}

/**
 * Hook to fetch sales statistics
 */
export function useSalesStats() {
  return useQuery({
    queryKey: [SALES_QUERY_KEY, 'stats'],
    queryFn: () => salesApi.getStats(),
  });
}

/**
 * Hook to fetch today's sales
 */
export function useTodaySales() {
  return useQuery({
    queryKey: [SALES_QUERY_KEY, 'today'],
    queryFn: () => salesApi.getToday(),
  });
}

/**
 * Hook to fetch sales by date range
 */
export function useSalesByDateRange(startDate: string, endDate: string) {
  return useQuery({
    queryKey: [SALES_QUERY_KEY, 'range', startDate, endDate],
    queryFn: () => salesApi.getByDateRange(startDate, endDate),
    enabled: !!startDate && !!endDate,
  });
}

/**
 * Hook to create a new sale
 */
export function useCreateSale() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: (data: CreateSaleDto) => salesApi.create(data),
    onSuccess: () => {
      // Invalidate sales list and stats
      queryClient.invalidateQueries({ queryKey: [SALES_QUERY_KEY] });
    },
  });
}

/**
 * Hook to update an existing sale
 */
export function useUpdateSale() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: ({ id, data }: { id: string; data: UpdateSaleDto }) =>
      salesApi.update(id, data),
    onSuccess: (_, variables) => {
      // Invalidate specific sale and sales list
      queryClient.invalidateQueries({ queryKey: [SALES_QUERY_KEY, variables.id] });
      queryClient.invalidateQueries({ queryKey: [SALES_QUERY_KEY] });
    },
  });
}

/**
 * Hook to cancel a sale
 */
export function useCancelSale() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: (id: string) => salesApi.cancel(id),
    onSuccess: () => {
      // Invalidate sales list
      queryClient.invalidateQueries({ queryKey: [SALES_QUERY_KEY] });
    },
  });
}

