import { useState, useCallback, useEffect } from 'react';
import { api } from '@/services/api';
import { ApiResponse } from '@/types/api.types';
import { RequestConfig } from '@/types/api.types';

interface UseApiOptions extends RequestConfig {
  onSuccess?: (data: any) => void;
  onError?: (error: any) => void;
}

/**
 * Hook for making API requests with loading and error states
 */
export function useApi<T = any>() {
  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const execute = useCallback(
    async (fn: () => Promise<ApiResponse<T>>, options?: UseApiOptions) => {
      setLoading(true);
      setError(null);

      try {
        const response = await fn();
        setData(response.data);
        options?.onSuccess?.(response.data);
        return response.data;
      } catch (err) {
        const errorMessage = err instanceof Error ? err.message : 'An error occurred';
        setError(errorMessage);
        options?.onError?.(err);
        throw err;
      } finally {
        setLoading(false);
      }
    },
    []
  );

  const get = useCallback(
    async (endpoint: string, options?: UseApiOptions) => {
      return execute(() => api.get<T>(endpoint, options), options);
    },
    [execute]
  );

  const post = useCallback(
    async (endpoint: string, data?: any, options?: UseApiOptions) => {
      return execute(() => api.post<T>(endpoint, data, options), options);
    },
    [execute]
  );

  const put = useCallback(
    async (endpoint: string, data?: any, options?: UseApiOptions) => {
      return execute(() => api.put<T>(endpoint, data, options), options);
    },
    [execute]
  );

  const patch = useCallback(
    async (endpoint: string, data?: any, options?: UseApiOptions) => {
      return execute(() => api.patch<T>(endpoint, data, options), options);
    },
    [execute]
  );

  const delete_ = useCallback(
    async (endpoint: string, options?: UseApiOptions) => {
      return execute(() => api.delete<T>(endpoint, options), options);
    },
    [execute]
  );

  const reset = useCallback(() => {
    setData(null);
    setLoading(false);
    setError(null);
  }, []);

  return {
    data,
    loading,
    error,
    get,
    post,
    put,
    patch,
    delete: delete_,
    reset,
  };
}

/**
 * Hook for fetching data on mount
 */
export function useFetch<T = any>(endpoint: string, options?: UseApiOptions) {
  const { data, loading, error, get } = useApi<T>();

  // Fetch on mount
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    if (!mounted) {
      setMounted(true);
      get(endpoint, options);
    }
  }, [endpoint, mounted, get, options]);

  return { data, loading, error };
}

import { useCallback } from 'react';
import { useAuthStore } from '@/store/authStore';
import { UserRole } from '@/types/auth.types';

/**
 * Hook to access auth state and actions
 */
export function useAuth() {
  const state = useAuthStore();

  const login = useCallback(
    (email: string, password: string) => state.login(email, password),
    [state]
  );

  const register = useCallback(
    (email: string, password: string, name: string, companyName?: string) =>
      state.register(email, password, name, companyName),
    [state]
  );

  const logout = useCallback(() => state.logout(), [state]);

  const refreshToken = useCallback(() => state.refreshToken(), [state]);

  const hasRole = useCallback(
    (role: UserRole | UserRole[]) => state.hasRole(role),
    [state]
  );

  const hasPermission = useCallback(
    (permission: string) => state.hasPermission(permission),
    [state]
  );

  return {
    // State
    user: state.user,
    isAuthenticated: state.isAuthenticated,
    loading: state.loading,
    error: state.error,

    // Actions
    login,
    register,
    logout,
    refreshToken,
    setUser: state.setUser,
    setError: state.setError,
    clearError: state.clearError,

    // Helpers
    hasRole,
    hasPermission,
    initializeAuth: state.initializeAuth,
  };
}

'use client';

import { useState } from 'react';
import { Pencil, Trash2, Eye } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import type { Product } from '@/types/product';

interface ProductsTableProps {
  products: Product[];
  onEdit: (product: Product) => void;
  onDelete: (product: Product) => void;
  onView: (product: Product) => void;
  isLoading?: boolean;
}

export function ProductsTable({
  products,
  onEdit,
  onDelete,
  onView,
  isLoading,
}: ProductsTableProps) {
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const getStockStatus = (product: Product) => {
    if (product.stock_quantity === 0) {
      return <Badge variant="error">Sin stock</Badge>;
    }
    if (product.min_stock_level && product.stock_quantity <= product.min_stock_level) {
      return <Badge variant="warning">Stock bajo</Badge>;
    }
    return <Badge variant="success">En stock</Badge>;
  };

  if (isLoading) {
    return (
      <div className="animate-pulse">
        <div className="h-12 bg-gray-200 rounded mb-4"></div>
        <div className="h-12 bg-gray-200 rounded mb-4"></div>
        <div className="h-12 bg-gray-200 rounded mb-4"></div>
      </div>
    );
  }

  if (products.length === 0) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500 text-lg">No se encontraron productos</p>
        <p className="text-gray-400 text-sm mt-2">
          Intenta ajustar los filtros o crear un nuevo producto
        </p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse">
        <thead>
          <tr className="border-b border-gray-200 bg-gray-50">
            <th className="text-left p-4 font-semibold text-gray-700">Producto</th>
            <th className="text-left p-4 font-semibold text-gray-700">SKU</th>
            <th className="text-left p-4 font-semibold text-gray-700">Categoría</th>
            <th className="text-right p-4 font-semibold text-gray-700">Precio</th>
            <th className="text-center p-4 font-semibold text-gray-700">Stock</th>
            <th className="text-center p-4 font-semibold text-gray-700">Estado</th>
            <th className="text-center p-4 font-semibold text-gray-700">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr
              key={product.id}
              className="border-b border-gray-100 hover:bg-gray-50 transition-colors"
            >
              <td className="p-4">
                <div className="flex items-center gap-3">
                  {product.image_url ? (
                    <img
                      src={product.image_url}
                      alt={product.name}
                      className="w-10 h-10 rounded object-cover"
                    />
                  ) : (
                    <div className="w-10 h-10 rounded bg-gray-200 flex items-center justify-center">
                      <span className="text-gray-400 text-xs">IMG</span>
                    </div>
                  )}
                  <div>
                    <div className="font-medium text-gray-900">{product.name}</div>
                    {product.description && (
                      <div className="text-sm text-gray-500 truncate max-w-xs">
                        {product.description}
                      </div>
                    )}
                  </div>
                </div>
              </td>
              <td className="p-4">
                <span className="font-mono text-sm text-gray-700">{product.sku}</span>
              </td>
              <td className="p-4">
                <span className="text-sm text-gray-600">
                  {product.category || '-'}
                </span>
              </td>
              <td className="p-4 text-right">
                <span className="font-semibold text-gray-900">
                  {formatCurrency(product.price)}
                </span>
              </td>
              <td className="p-4 text-center">
                <div>
                  <span className="font-semibold text-gray-900">
                    {product.stock_quantity}
                  </span>
                  {product.unit_of_measure && (
                    <span className="text-sm text-gray-500 ml-1">
                      {product.unit_of_measure}
                    </span>
                  )}
                </div>
                {getStockStatus(product)}
              </td>
              <td className="p-4 text-center">
                {product.is_active ? (
                  <Badge variant="success">Activo</Badge>
                ) : (
                  <Badge variant="default">Inactivo</Badge>
                )}
              </td>
              <td className="p-4">
                <div className="flex items-center justify-center gap-2">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onView(product)}
                    title="Ver detalles"
                  >
                    <Eye className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(product)}
                    title="Editar"
                  >
                    <Pencil className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(product)}
                    title="Eliminar"
                    className="text-red-600 hover:text-red-700 hover:bg-red-50"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Search, X, Filter } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import type { ProductQueryParams } from '@/types/product';

interface ProductFiltersProps {
  onFilterChange: (filters: ProductQueryParams) => void;
  categories?: string[];
}

export function ProductFilters({ onFilterChange, categories = [] }: ProductFiltersProps) {
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('');
  const [isActive, setIsActive] = useState<boolean | undefined>(undefined);
  const [sortBy, setSortBy] = useState<ProductQueryParams['sortBy']>('created_at');
  const [sortOrder, setSortOrder] = useState<'ASC' | 'DESC'>('DESC');
  const [showAdvanced, setShowAdvanced] = useState(false);

  const handleSearch = () => {
    onFilterChange({
      search: search || undefined,
      category: category || undefined,
      isActive,
      sortBy,
      sortOrder,
    });
  };

  const handleReset = () => {
    setSearch('');
    setCategory('');
    setIsActive(undefined);
    setSortBy('created_at');
    setSortOrder('DESC');
    onFilterChange({});
  };

  const hasActiveFilters =
    search || category || isActive !== undefined || sortBy !== 'created_at';

  return (
    <div className="bg-white p-4 rounded-lg border border-gray-200 space-y-4">
      {/* Búsqueda principal */}
      <div className="flex gap-3">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
          <Input
            type="text"
            placeholder="Buscar por nombre, SKU o código de barras..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
            className="pl-10"
          />
        </div>
        <Button onClick={handleSearch}>Buscar</Button>
        {hasActiveFilters && (
          <Button variant="outline" onClick={handleReset}>
            <X className="w-4 h-4 mr-2" />
            Limpiar
          </Button>
        )}
        <Button
          variant="outline"
          onClick={() => setShowAdvanced(!showAdvanced)}
          className={showAdvanced ? 'bg-gray-100' : ''}
        >
          <Filter className="w-4 h-4 mr-2" />
          Filtros
        </Button>
      </div>

      {/* Filtros avanzados */}
      {showAdvanced && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 pt-4 border-t">
          {/* Categoría */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Categoría
            </label>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Todas</option>
              {categories.map((cat) => (
                <option key={cat} value={cat}>
                  {cat}
                </option>
              ))}
            </select>
          </div>

          {/* Estado */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Estado
            </label>
            <select
              value={isActive === undefined ? '' : isActive ? 'true' : 'false'}
              onChange={(e) =>
                setIsActive(
                  e.target.value === '' ? undefined : e.target.value === 'true'
                )
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Todos</option>
              <option value="true">Activos</option>
              <option value="false">Inactivos</option>
            </select>
          </div>

          {/* Ordenar por */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Ordenar por
            </label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value as ProductQueryParams['sortBy'])}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="created_at">Fecha de creación</option>
              <option value="name">Nombre</option>
              <option value="price">Precio</option>
              <option value="stock_quantity">Stock</option>
            </select>
          </div>

          {/* Orden */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Orden
            </label>
            <select
              value={sortOrder}
              onChange={(e) => setSortOrder(e.target.value as 'ASC' | 'DESC')}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="ASC">Ascendente</option>
              <option value="DESC">Descendente</option>
            </select>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import type { Product, CreateProductDto, UpdateProductDto } from '@/types/product';

interface ProductFormProps {
  product?: Product;
  onSubmit: (data: CreateProductDto | UpdateProductDto) => void;
  onCancel: () => void;
  isLoading?: boolean;
}

export function ProductForm({
  product,
  onSubmit,
  onCancel,
  isLoading,
}: ProductFormProps) {
  const [formData, setFormData] = useState<CreateProductDto>({
    name: '',
    description: '',
    sku: '',
    barcode: '',
    category: '',
    price: 0,
    cost: 0,
    stock_quantity: 0,
    min_stock_level: 0,
    max_stock_level: 0,
    unit_of_measure: 'unidad',
    tax_rate: 19,
    image_url: '',
  });

  useEffect(() => {
    if (product) {
      setFormData({
        name: product.name,
        description: product.description || '',
        sku: product.sku,
        barcode: product.barcode || '',
        category: product.category || '',
        price: product.price,
        cost: product.cost || 0,
        stock_quantity: product.stock_quantity,
        min_stock_level: product.min_stock_level || 0,
        max_stock_level: product.max_stock_level || 0,
        unit_of_measure: product.unit_of_measure || 'unidad',
        tax_rate: product.tax_rate || 19,
        image_url: product.image_url || '',
      });
    }
  }, [product]);

  const handleChange = (field: keyof CreateProductDto, value: string | number) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Nombre */}
        <div className="md:col-span-2">
          <Label htmlFor="name">
            Nombre del producto <span className="text-red-500">*</span>
          </Label>
          <Input
            id="name"
            type="text"
            value={formData.name}
            onChange={(e) => handleChange('name', e.target.value)}
            placeholder="Ej: Laptop Dell Inspiron 15"
            required
          />
        </div>

        {/* Descripción */}
        <div className="md:col-span-2">
          <Label htmlFor="description">Descripción</Label>
          <textarea
            id="description"
            value={formData.description}
            onChange={(e) => handleChange('description', e.target.value)}
            placeholder="Descripción del producto..."
            rows={3}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* SKU */}
        <div>
          <Label htmlFor="sku">
            SKU <span className="text-red-500">*</span>
          </Label>
          <Input
            id="sku"
            type="text"
            value={formData.sku}
            onChange={(e) => handleChange('sku', e.target.value)}
            placeholder="PRD-001"
            required
          />
        </div>

        {/* Código de barras */}
        <div>
          <Label htmlFor="barcode">Código de barras</Label>
          <Input
            id="barcode"
            type="text"
            value={formData.barcode}
            onChange={(e) => handleChange('barcode', e.target.value)}
            placeholder="1234567890123"
          />
        </div>

        {/* Categoría */}
        <div>
          <Label htmlFor="category">Categoría</Label>
          <Input
            id="category"
            type="text"
            value={formData.category}
            onChange={(e) => handleChange('category', e.target.value)}
            placeholder="Electrónica"
          />
        </div>

        {/* Unidad de medida */}
        <div>
          <Label htmlFor="unit_of_measure">Unidad de medida</Label>
          <select
            id="unit_of_measure"
            value={formData.unit_of_measure}
            onChange={(e) => handleChange('unit_of_measure', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="unidad">Unidad</option>
            <option value="kg">Kilogramo</option>
            <option value="lb">Libra</option>
            <option value="lt">Litro</option>
            <option value="mt">Metro</option>
            <option value="caja">Caja</option>
            <option value="paquete">Paquete</option>
          </select>
        </div>

        {/* Precio */}
        <div>
          <Label htmlFor="price">
            Precio de venta <span className="text-red-500">*</span>
          </Label>
          <Input
            id="price"
            type="number"
            step="0.01"
            min="0"
            value={formData.price}
            onChange={(e) => handleChange('price', parseFloat(e.target.value) || 0)}
            placeholder="0.00"
            required
          />
        </div>

        {/* Costo */}
        <div>
          <Label htmlFor="cost">Costo</Label>
          <Input
            id="cost"
            type="number"
            step="0.01"
            min="0"
            value={formData.cost}
            onChange={(e) => handleChange('cost', parseFloat(e.target.value) || 0)}
            placeholder="0.00"
          />
        </div>

        {/* Cantidad en stock */}
        <div>
          <Label htmlFor="stock_quantity">
            Cantidad en stock <span className="text-red-500">*</span>
          </Label>
          <Input
            id="stock_quantity"
            type="number"
            min="0"
            value={formData.stock_quantity}
            onChange={(e) =>
              handleChange('stock_quantity', parseInt(e.target.value) || 0)
            }
            placeholder="0"
            required
          />
        </div>

        {/* Stock mínimo */}
        <div>
          <Label htmlFor="min_stock_level">Stock mínimo</Label>
          <Input
            id="min_stock_level"
            type="number"
            min="0"
            value={formData.min_stock_level}
            onChange={(e) =>
              handleChange('min_stock_level', parseInt(e.target.value) || 0)
            }
            placeholder="0"
          />
        </div>

        {/* Stock máximo */}
        <div>
          <Label htmlFor="max_stock_level">Stock máximo</Label>
          <Input
            id="max_stock_level"
            type="number"
            min="0"
            value={formData.max_stock_level}
            onChange={(e) =>
              handleChange('max_stock_level', parseInt(e.target.value) || 0)
            }
            placeholder="0"
          />
        </div>

        {/* Tasa de impuesto */}
        <div>
          <Label htmlFor="tax_rate">Tasa de impuesto (%)</Label>
          <Input
            id="tax_rate"
            type="number"
            step="0.01"
            min="0"
            max="100"
            value={formData.tax_rate}
            onChange={(e) => handleChange('tax_rate', parseFloat(e.target.value) || 0)}
            placeholder="19"
          />
        </div>

        {/* URL de imagen */}
        <div className="md:col-span-2">
          <Label htmlFor="image_url">URL de imagen</Label>
          <Input
            id="image_url"
            type="url"
            value={formData.image_url}
            onChange={(e) => handleChange('image_url', e.target.value)}
            placeholder="https://example.com/image.jpg"
          />
          {formData.image_url && (
            <div className="mt-2">
              <img
                src={formData.image_url}
                alt="Preview"
                className="w-32 h-32 object-cover rounded border"
                onError={(e) => {
                  e.currentTarget.style.display = 'none';
                }}
              />
            </div>
          )}
        </div>
      </div>

      {/* Botones */}
      <div className="flex justify-end gap-3 pt-4 border-t">
        <Button type="button" variant="outline" onClick={onCancel} disabled={isLoading}>
          Cancelar
        </Button>
        <Button type="submit" disabled={isLoading}>
          {isLoading ? 'Guardando...' : product ? 'Actualizar' : 'Crear producto'}
        </Button>
      </div>
    </form>
  );
}

