import { apiClient } from './client';
import type {
  Customer,
  CreateCustomerDto,
  UpdateCustomerDto,
  CustomerQueryParams,
  CustomersResponse,
  CustomerStats,
  UpdateLoyaltyPointsDto,
  PurchaseHistory,
} from '@/types/customer';

export const customersApi = {
  /**
   * Get all customers with pagination and filters
   */
  getAll: async (params?: CustomerQueryParams): Promise<CustomersResponse> => {
    const searchParams = new URLSearchParams();

    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.pageSize) searchParams.append('pageSize', params.pageSize.toString());
    if (params?.search) searchParams.append('search', params.search);
    if (params?.isActive !== undefined) searchParams.append('isActive', params.isActive.toString());
    if (params?.minLoyaltyPoints !== undefined) searchParams.append('minLoyaltyPoints', params.minLoyaltyPoints.toString());
    if (params?.sortBy) searchParams.append('sortBy', params.sortBy);
    if (params?.sortOrder) searchParams.append('sortOrder', params.sortOrder);

    const response = await apiClient.get(`/customers?${searchParams.toString()}`);
    return response.data;
  },

  /**
   * Get a single customer by ID
   */
  getById: async (id: string): Promise<Customer> => {
    const response = await apiClient.get(`/customers/${id}`);
    return response.data;
  },

  /**
   * Create a new customer
   */
  create: async (data: CreateCustomerDto): Promise<Customer> => {
    const response = await apiClient.post('/customers', data);
    return response.data;
  },

  /**
   * Update an existing customer
   */
  update: async (id: string, data: UpdateCustomerDto): Promise<Customer> => {
    const response = await apiClient.patch(`/customers/${id}`, data);
    return response.data;
  },

  /**
   * Delete a customer (soft delete)
   */
  delete: async (id: string): Promise<void> => {
    await apiClient.delete(`/customers/${id}`);
  },

  /**
   * Get customer statistics
   */
  getStats: async (): Promise<CustomerStats> => {
    const response = await apiClient.get('/customers/stats');
    return response.data;
  },

  /**
   * Get top customers by total purchases
   */
  getTopCustomers: async (limit: number = 10): Promise<Customer[]> => {
    const response = await apiClient.get(`/customers/top?limit=${limit}`);
    return response.data;
  },

  /**
   * Advanced customer search
   */
  search: async (params: CustomerQueryParams): Promise<Customer[]> => {
    const searchParams = new URLSearchParams();

    if (params.search) searchParams.append('search', params.search);
    if (params.isActive !== undefined) searchParams.append('isActive', params.isActive.toString());
    if (params.minLoyaltyPoints !== undefined) searchParams.append('minLoyaltyPoints', params.minLoyaltyPoints.toString());
    if (params.sortBy) searchParams.append('sortBy', params.sortBy);
    if (params.sortOrder) searchParams.append('sortOrder', params.sortOrder);

    const response = await apiClient.get(`/customers/search?${searchParams.toString()}`);
    return response.data;
  },

  /**
   * Get customer purchase history
   */
  getPurchaseHistory: async (id: string, limit: number = 10): Promise<PurchaseHistory[]> => {
    const response = await apiClient.get(`/customers/${id}/purchase-history?limit=${limit}`);
    return response.data;
  },

  /**
   * Update customer loyalty points
   */
  updateLoyaltyPoints: async (id: string, data: UpdateLoyaltyPointsDto): Promise<Customer> => {
    const response = await apiClient.patch(`/customers/${id}/loyalty`, data);
    return response.data;
  },
};

import { apiClient } from './client';
import type {
  StockLevel,
  StockMovement,
  Warehouse,
  WarehouseLocation,
  AdjustStockDto,
  CreateWarehouseDto,
  UpdateWarehouseDto,
  CreateLocationDto,
  StockQueryParams,
  MovementQueryParams,
  StockResponse,
  MovementsResponse,
  InventoryStats,
} from '@/types/inventory';

export const inventoryApi = {
  /**
   * Get current stock levels
   */
  getStock: async (params?: StockQueryParams): Promise<StockResponse> => {
    const searchParams = new URLSearchParams();

    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.pageSize) searchParams.append('pageSize', params.pageSize.toString());
    if (params?.warehouse_id) searchParams.append('warehouse_id', params.warehouse_id);
    if (params?.product_id) searchParams.append('product_id', params.product_id);
    if (params?.low_stock) searchParams.append('low_stock', params.low_stock.toString());
    if (params?.sortBy) searchParams.append('sortBy', params.sortBy);
    if (params?.sortOrder) searchParams.append('sortOrder', params.sortOrder);

    const response = await apiClient.get(`/inventory/stock?${searchParams.toString()}`);
    return response.data;
  },

  /**
   * Get stock for a specific product
   */
  getStockByProduct: async (productId: string): Promise<StockLevel[]> => {
    const response = await apiClient.get(`/inventory/stock/${productId}`);
    return response.data;
  },

  /**
   * Adjust stock (IN, OUT, ADJUST, etc.)
   */
  adjustStock: async (data: AdjustStockDto): Promise<StockMovement> => {
    const response = await apiClient.post('/inventory/adjust', data);
    return response.data;
  },

  /**
   * Get stock movements
   */
  getMovements: async (params?: MovementQueryParams): Promise<MovementsResponse> => {
    const searchParams = new URLSearchParams();

    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.pageSize) searchParams.append('pageSize', params.pageSize.toString());
    if (params?.warehouse_id) searchParams.append('warehouse_id', params.warehouse_id);
    if (params?.product_id) searchParams.append('product_id', params.product_id);
    if (params?.movement_type) searchParams.append('movement_type', params.movement_type);
    if (params?.start_date) searchParams.append('start_date', params.start_date);
    if (params?.end_date) searchParams.append('end_date', params.end_date);
    if (params?.sortOrder) searchParams.append('sortOrder', params.sortOrder);

    const response = await apiClient.get(`/inventory/movements?${searchParams.toString()}`);
    return response.data;
  },

  /**
   * Get a single movement by ID
   */
  getMovementById: async (id: string): Promise<StockMovement> => {
    const response = await apiClient.get(`/inventory/movements/${id}`);
    return response.data;
  },

  /**
   * Get all warehouses
   */
  getWarehouses: async (): Promise<Warehouse[]> => {
    const response = await apiClient.get('/inventory/warehouses');
    return response.data;
  },

  /**
   * Create a warehouse
   */
  createWarehouse: async (data: CreateWarehouseDto): Promise<Warehouse> => {
    const response = await apiClient.post('/inventory/warehouses', data);
    return response.data;
  },

  /**
   * Get warehouse by ID
   */
  getWarehouseById: async (id: string): Promise<Warehouse> => {
    const response = await apiClient.get(`/inventory/warehouses/${id}`);
    return response.data;
  },

  /**
   * Update warehouse
   */
  updateWarehouse: async (id: string, data: UpdateWarehouseDto): Promise<Warehouse> => {
    const response = await apiClient.put(`/inventory/warehouses/${id}`, data);
    return response.data;
  },

  /**
   * Get locations for a warehouse
   */
  getLocations: async (warehouseId: string): Promise<WarehouseLocation[]> => {
    const response = await apiClient.get(`/inventory/locations/${warehouseId}`);
    return response.data;
  },

  /**
   * Create a warehouse location
   */
  createLocation: async (data: CreateLocationDto): Promise<WarehouseLocation> => {
    const response = await apiClient.post('/inventory/locations', data);
    return response.data;
  },

  /**
   * Update warehouse location
   */
  updateLocation: async (
    id: string,
    data: Partial<CreateLocationDto>
  ): Promise<WarehouseLocation> => {
    const response = await apiClient.put(`/inventory/locations/${id}`, data);
    return response.data;
  },

  /**
   * Get inventory statistics
   */
  getStats: async (): Promise<InventoryStats> => {
    const response = await apiClient.get('/inventory/stats');
    return response.data;
  },
};

import { apiClient } from './client';
import type {
  Sale,
  CreateSaleDto,
  UpdateSaleDto,
  SaleQueryParams,
  SalesResponse,
  SalesStats,
} from '@/types/sale';

export const salesApi = {
  /**
   * Get all sales with pagination and filters
   */
  getAll: async (params?: SaleQueryParams): Promise<SalesResponse> => {
    const searchParams = new URLSearchParams();

    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.pageSize) searchParams.append('pageSize', params.pageSize.toString());
    if (params?.status) searchParams.append('status', params.status);
    if (params?.payment_status) searchParams.append('payment_status', params.payment_status);
    if (params?.customer_id) searchParams.append('customer_id', params.customer_id);
    if (params?.start_date) searchParams.append('start_date', params.start_date);
    if (params?.end_date) searchParams.append('end_date', params.end_date);
    if (params?.sortBy) searchParams.append('sortBy', params.sortBy);
    if (params?.sortOrder) searchParams.append('sortOrder', params.sortOrder);

    const response = await apiClient.get(`/sales?${searchParams.toString()}`);
    return response.data;
  },

  /**
   * Get a single sale by ID
   */
  getById: async (id: string): Promise<Sale> => {
    const response = await apiClient.get(`/sales/${id}`);
    return response.data;
  },

  /**
   * Create a new sale
   */
  create: async (data: CreateSaleDto): Promise<Sale> => {
    const response = await apiClient.post('/sales', data);
    return response.data;
  },

  /**
   * Update an existing sale
   */
  update: async (id: string, data: UpdateSaleDto): Promise<Sale> => {
    const response = await apiClient.patch(`/sales/${id}`, data);
    return response.data;
  },

  /**
   * Cancel a sale
   */
  cancel: async (id: string): Promise<Sale> => {
    const response = await apiClient.patch(`/sales/${id}/cancel`);
    return response.data;
  },

  /**
   * Get sales statistics
   */
  getStats: async (): Promise<SalesStats> => {
    const response = await apiClient.get('/sales/stats');
    return response.data;
  },

  /**
   * Get today's sales
   */
  getToday: async (): Promise<Sale[]> => {
    const response = await apiClient.get('/sales/today');
    return response.data;
  },

  /**
   * Get sales by date range
   */
  getByDateRange: async (startDate: string, endDate: string): Promise<Sale[]> => {
    const response = await apiClient.get(
      `/sales/range?start_date=${startDate}&end_date=${endDate}`
    );
    return response.data;
  },
};

