'use client';

import { useState } from 'react';
import { Search, X, Filter } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import type { CustomerQueryParams } from '@/types/customer';

interface CustomerFiltersProps {
  onFilterChange: (filters: CustomerQueryParams) => void;
}

export function CustomerFilters({ onFilterChange }: CustomerFiltersProps) {
  const [search, setSearch] = useState('');
  const [isActive, setIsActive] = useState<boolean | undefined>(undefined);
  const [minLoyaltyPoints, setMinLoyaltyPoints] = useState<number | undefined>(undefined);
  const [sortBy, setSortBy] = useState<CustomerQueryParams['sortBy']>('created_at');
  const [sortOrder, setSortOrder] = useState<'ASC' | 'DESC'>('DESC');
  const [showAdvanced, setShowAdvanced] = useState(false);

  const handleSearch = () => {
    onFilterChange({
      search: search || undefined,
      isActive,
      minLoyaltyPoints,
      sortBy,
      sortOrder,
    });
  };

  const handleReset = () => {
    setSearch('');
    setIsActive(undefined);
    setMinLoyaltyPoints(undefined);
    setSortBy('created_at');
    setSortOrder('DESC');
    onFilterChange({});
  };

  const hasActiveFilters =
    search || isActive !== undefined || minLoyaltyPoints !== undefined || sortBy !== 'created_at';

  return (
    <div className="bg-white p-4 rounded-lg border border-gray-200 space-y-4">
      {/* Búsqueda principal */}
      <div className="flex gap-3">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
          <Input
            type="text"
            placeholder="Buscar por nombre, email o teléfono..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
            className="pl-10"
          />
        </div>
        <Button onClick={handleSearch}>Buscar</Button>
        {hasActiveFilters && (
          <Button variant="outline" onClick={handleReset}>
            <X className="w-4 h-4 mr-2" />
            Limpiar
          </Button>
        )}
        <Button
          variant="outline"
          onClick={() => setShowAdvanced(!showAdvanced)}
          className={showAdvanced ? 'bg-gray-100' : ''}
        >
          <Filter className="w-4 h-4 mr-2" />
          Filtros
        </Button>
      </div>

      {/* Filtros avanzados */}
      {showAdvanced && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 pt-4 border-t">
          {/* Estado */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Estado
            </label>
            <select
              value={isActive === undefined ? '' : isActive ? 'true' : 'false'}
              onChange={(e) =>
                setIsActive(
                  e.target.value === '' ? undefined : e.target.value === 'true'
                )
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Todos</option>
              <option value="true">Activos</option>
              <option value="false">Inactivos</option>
            </select>
          </div>

          {/* Puntos mínimos */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Puntos mínimos
            </label>
            <Input
              type="number"
              min="0"
              value={minLoyaltyPoints || ''}
              onChange={(e) =>
                setMinLoyaltyPoints(
                  e.target.value ? parseInt(e.target.value) : undefined
                )
              }
              placeholder="0"
            />
          </div>

          {/* Ordenar por */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Ordenar por
            </label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value as CustomerQueryParams['sortBy'])}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="created_at">Fecha de registro</option>
              <option value="name">Nombre</option>
              <option value="total_purchases">Total compras</option>
              <option value="loyalty_points">Puntos de lealtad</option>
            </select>
          </div>

          {/* Orden */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Orden
            </label>
            <select
              value={sortOrder}
              onChange={(e) => setSortOrder(e.target.value as 'ASC' | 'DESC')}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="ASC">Ascendente</option>
              <option value="DESC">Descendente</option>
            </select>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import type { Customer, CreateCustomerDto, UpdateCustomerDto } from '@/types/customer';

interface CustomerFormProps {
  customer?: Customer;
  onSubmit: (data: CreateCustomerDto) => void | Promise<void>;
  onCancel: () => void;
  isLoading?: boolean;
}

export function CustomerForm({
  customer,
  onSubmit,
  onCancel,
  isLoading,
}: CustomerFormProps) {
  const [formData, setFormData] = useState<CreateCustomerDto>({
    name: '',
    email: '',
    phone: '',
    address: '',
  });

  useEffect(() => {
    if (customer) {
      setFormData({
        name: customer.name,
        email: customer.email || '',
        phone: customer.phone || '',
        address: customer.address || '',
      });
    }
  }, [customer]);

  const handleChange = (field: keyof CreateCustomerDto, value: string) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Remove empty optional fields
    const submitData: CreateCustomerDto = {
      name: formData.name,
    };

    if (formData.email && formData.email.trim()) {
      submitData.email = formData.email.trim();
    }
    if (formData.phone && formData.phone.trim()) {
      submitData.phone = formData.phone.trim();
    }
    if (formData.address && formData.address.trim()) {
      submitData.address = formData.address.trim();
    }

    onSubmit(submitData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Nombre */}
        <div className="md:col-span-2">
          <Label htmlFor="name">
            Nombre completo <span className="text-red-500">*</span>
          </Label>
          <Input
            id="name"
            type="text"
            value={formData.name}
            onChange={(e) => handleChange('name', e.target.value)}
            placeholder="Ej: Juan Pérez García"
            required
          />
        </div>

        {/* Email */}
        <div>
          <Label htmlFor="email">Email</Label>
          <Input
            id="email"
            type="email"
            value={formData.email}
            onChange={(e) => handleChange('email', e.target.value)}
            placeholder="ejemplo@correo.com"
          />
          <p className="text-xs text-gray-500 mt-1">
            Usado para enviar recibos y notificaciones
          </p>
        </div>

        {/* Teléfono */}
        <div>
          <Label htmlFor="phone">Teléfono</Label>
          <Input
            id="phone"
            type="tel"
            value={formData.phone}
            onChange={(e) => handleChange('phone', e.target.value)}
            placeholder="+57 300 123 4567"
          />
          <p className="text-xs text-gray-500 mt-1">
            Incluye código de país (+57 para Colombia)
          </p>
        </div>

        {/* Dirección */}
        <div className="md:col-span-2">
          <Label htmlFor="address">Dirección</Label>
          <textarea
            id="address"
            value={formData.address}
            onChange={(e) => handleChange('address', e.target.value)}
            placeholder="Calle 123 #45-67, Apartamento 8B, Ciudad"
            rows={3}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <p className="text-xs text-gray-500 mt-1">
            Dirección de envío o facturación
          </p>
        </div>
      </div>

      {/* Info adicional para edición */}
      {customer && (
        <div className="bg-blue-50 rounded-lg p-4 border border-blue-200">
          <h4 className="font-semibold text-blue-900 mb-2">Información adicional</h4>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <span className="text-blue-700">Puntos de lealtad:</span>
              <span className="ml-2 font-semibold text-blue-900">
                {customer.loyalty_points}
              </span>
            </div>
            <div>
              <span className="text-blue-700">Total compras:</span>
              <span className="ml-2 font-semibold text-blue-900">
                {new Intl.NumberFormat('es-CO', {
                  style: 'currency',
                  currency: 'COP',
                  minimumFractionDigits: 0,
                }).format(customer.total_purchases)}
              </span>
            </div>
          </div>
          <p className="text-xs text-blue-600 mt-2">
            * Los puntos de lealtad se gestionan desde el botón de acción en la tabla
          </p>
        </div>
      )}

      {/* Botones */}
      <div className="flex justify-end gap-3 pt-4 border-t">
        <Button type="button" variant="outline" onClick={onCancel} disabled={isLoading}>
          Cancelar
        </Button>
        <Button type="submit" disabled={isLoading || !formData.name.trim()}>
          {isLoading ? 'Guardando...' : customer ? 'Actualizar' : 'Crear cliente'}
        </Button>
      </div>
    </form>
  );
}

'use client';

import { Pencil, Trash2, Eye, Award, ShoppingBag } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import type { Customer } from '@/types/customer';

interface CustomersTableProps {
  customers: Customer[];
  onEdit: (customer: Customer) => void;
  onDelete: (customer: Customer) => void;
  onView: (customer: Customer) => void;
  onManageLoyalty: (customer: Customer) => void;
  isLoading?: boolean;
}

export function CustomersTable({
  customers,
  onEdit,
  onDelete,
  onView,
  onManageLoyalty,
  isLoading,
}: CustomersTableProps) {
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('es-CO', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    }).format(date);
  };

  if (isLoading) {
    return (
      <div className="animate-pulse space-y-4">
        <div className="h-12 bg-gray-200 rounded"></div>
        <div className="h-12 bg-gray-200 rounded"></div>
        <div className="h-12 bg-gray-200 rounded"></div>
      </div>
    );
  }

  if (customers.length === 0) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500 text-lg">No se encontraron clientes</p>
        <p className="text-gray-400 text-sm mt-2">
          Intenta ajustar los filtros o crear un nuevo cliente
        </p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse">
        <thead>
          <tr className="border-b border-gray-200 bg-gray-50">
            <th className="text-left p-4 font-semibold text-gray-700">Cliente</th>
            <th className="text-left p-4 font-semibold text-gray-700">Contacto</th>
            <th className="text-right p-4 font-semibold text-gray-700">Compras</th>
            <th className="text-center p-4 font-semibold text-gray-700">Puntos</th>
            <th className="text-center p-4 font-semibold text-gray-700">Última compra</th>
            <th className="text-center p-4 font-semibold text-gray-700">Estado</th>
            <th className="text-center p-4 font-semibold text-gray-700">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer) => (
            <tr
              key={customer.id}
              className="border-b border-gray-100 hover:bg-gray-50 transition-colors"
            >
              <td className="p-4">
                <div>
                  <div className="font-medium text-gray-900">{customer.name}</div>
                  {customer.address && (
                    <div className="text-sm text-gray-500 truncate max-w-xs">
                      {customer.address}
                    </div>
                  )}
                </div>
              </td>
              <td className="p-4">
                <div className="text-sm">
                  {customer.email && (
                    <div className="text-gray-700">{customer.email}</div>
                  )}
                  {customer.phone && (
                    <div className="text-gray-500">{customer.phone}</div>
                  )}
                  {!customer.email && !customer.phone && (
                    <div className="text-gray-400">-</div>
                  )}
                </div>
              </td>
              <td className="p-4 text-right">
                <div>
                  <div className="font-semibold text-gray-900">
                    {formatCurrency(customer.total_purchases)}
                  </div>
                  <div className="text-sm text-gray-500">Total</div>
                </div>
              </td>
              <td className="p-4 text-center">
                <div className="inline-flex items-center gap-1">
                  <Award className="w-4 h-4 text-yellow-500" />
                  <span className="font-semibold text-gray-900">
                    {customer.loyalty_points}
                  </span>
                </div>
              </td>
              <td className="p-4 text-center">
                <span className="text-sm text-gray-600">
                  {formatDate(customer.last_purchase_date)}
                </span>
              </td>
              <td className="p-4 text-center">
                {customer.is_active ? (
                  <Badge variant="success">Activo</Badge>
                ) : (
                  <Badge variant="default">Inactivo</Badge>
                )}
              </td>
              <td className="p-4">
                <div className="flex items-center justify-center gap-2">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onView(customer)}
                    title="Ver detalles"
                  >
                    <Eye className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onManageLoyalty(customer)}
                    title="Gestionar puntos"
                    className="text-yellow-600 hover:text-yellow-700 hover:bg-yellow-50"
                  >
                    <Award className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(customer)}
                    title="Editar"
                  >
                    <Pencil className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(customer)}
                    title="Eliminar"
                    className="text-red-600 hover:text-red-700 hover:bg-red-50"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </td>
            </tr>
          ))}
