'use client';

import { Package, AlertTriangle, ArrowUpCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import type { StockLevel } from '@/types/inventory';

interface StockTableProps {
  stock: StockLevel[];
  onAdjust: (stock: StockLevel) => void;
  isLoading?: boolean;
}

export function StockTable({ stock, onAdjust, isLoading }: StockTableProps) {
  const getStockStatus = (item: StockLevel) => {
    if (item.quantity === 0) {
      return { label: 'Sin stock', variant: 'error' as const, icon: AlertTriangle };
    }
    if (item.reorder_point && item.quantity <= item.reorder_point) {
      return { label: 'Reabastecer', variant: 'warning' as const, icon: AlertTriangle };
    }
    if (item.min_stock_level && item.quantity <= item.min_stock_level) {
      return { label: 'Stock bajo', variant: 'warning' as const, icon: AlertTriangle };
    }
    return { label: 'Disponible', variant: 'success' as const, icon: Package };
  };

  if (isLoading) {
    return (
      <div className="animate-pulse space-y-4">
        <div className="h-12 bg-gray-200 rounded"></div>
        <div className="h-12 bg-gray-200 rounded"></div>
        <div className="h-12 bg-gray-200 rounded"></div>
      </div>
    );
  }

  if (stock.length === 0) {
    return (
      <div className="text-center py-12">
        <Package className="w-16 h-16 mx-auto text-gray-300 mb-4" />
        <p className="text-gray-500 text-lg">No hay stock disponible</p>
        <p className="text-gray-400 text-sm mt-2">
          Ajusta filtros o agrega productos al inventario
        </p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse">
        <thead>
          <tr className="border-b border-gray-200 bg-gray-50">
            <th className="text-left p-4 font-semibold text-gray-700">Producto</th>
            <th className="text-left p-4 font-semibold text-gray-700">Almacén</th>
            <th className="text-center p-4 font-semibold text-gray-700">Cantidad</th>
            <th className="text-center p-4 font-semibold text-gray-700">Reservado</th>
            <th className="text-center p-4 font-semibold text-gray-700">Disponible</th>
            <th className="text-center p-4 font-semibold text-gray-700">Estado</th>
            <th className="text-center p-4 font-semibold text-gray-700">Acción</th>
          </tr>
        </thead>
        <tbody>
          {stock.map((item) => {
            const status = getStockStatus(item);
            const StatusIcon = status.icon;

            return (
              <tr
                key={item.id}
                className="border-b border-gray-100 hover:bg-gray-50 transition-colors"
              >
                <td className="p-4">
                  <div>
                    <div className="font-medium text-gray-900">
                      {item.product_name || 'Producto sin nombre'}
                    </div>
                    {item.product_sku && (
                      <div className="text-sm text-gray-500 font-mono">
                        SKU: {item.product_sku}
                      </div>
                    )}
                  </div>
                </td>
                <td className="p-4">
                  <div>
                    <div className="text-gray-900">{item.warehouse_name}</div>
                    {item.location_name && (
                      <div className="text-sm text-gray-500">{item.location_name}</div>
                    )}
                  </div>
                </td>
                <td className="p-4 text-center">
                  <div className="font-semibold text-gray-900 text-lg">
                    {item.quantity}
                  </div>
                  {item.min_stock_level && (
                    <div className="text-xs text-gray-500">
                      Min: {item.min_stock_level}
                    </div>
                  )}
                </td>
                <td className="p-4 text-center">
                  <span className="text-gray-700">{item.reserved_quantity}</span>
                </td>
                <td className="p-4 text-center">
                  <span className="font-semibold text-green-600">
                    {item.available_quantity}
                  </span>
                </td>
                <td className="p-4 text-center">
                  <Badge variant={status.variant} className="inline-flex items-center gap-1">
                    <StatusIcon className="w-3 h-3" />
                    {status.label}
                  </Badge>
                </td>
                <td className="p-4 text-center">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => onAdjust(item)}
                    title="Ajustar stock"
                  >
                    <ArrowUpCircle className="w-4 h-4 mr-1" />
                    Ajustar
                  </Button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Filter, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import type { StockQueryParams } from '@/types/inventory';
import type { Warehouse } from '@/types/inventory';

interface InventoryFiltersProps {
  onFilterChange: (filters: StockQueryParams) => void;
  warehouses?: Warehouse[];
}

export function InventoryFilters({ onFilterChange, warehouses = [] }: InventoryFiltersProps) {
  const [warehouseId, setWarehouseId] = useState<string>('');
  const [lowStock, setLowStock] = useState<boolean | undefined>(undefined);
  const [sortBy, setSortBy] = useState<StockQueryParams['sortBy']>('product_name');
  const [sortOrder, setSortOrder] = useState<'ASC' | 'DESC'>('ASC');
  const [showAdvanced, setShowAdvanced] = useState(false);

  const handleApply = () => {
    onFilterChange({
      warehouse_id: warehouseId || undefined,
      low_stock: lowStock,
      sortBy,
      sortOrder,
    });
  };

  const handleReset = () => {
    setWarehouseId('');
    setLowStock(undefined);
    setSortBy('product_name');
    setSortOrder('ASC');
    onFilterChange({});
  };

  const hasActiveFilters = warehouseId || lowStock !== undefined || sortBy !== 'product_name';

  return (
    <div className="bg-white p-4 rounded-lg border border-gray-200 space-y-4">
      {/* Main filters */}
      <div className="flex gap-3">
        <Button
          variant="outline"
          onClick={() => setShowAdvanced(!showAdvanced)}
          className={showAdvanced ? 'bg-gray-100' : ''}
        >
          <Filter className="w-4 h-4 mr-2" />
          Filtros
        </Button>
        {hasActiveFilters && (
          <Button variant="outline" onClick={handleReset}>
            <X className="w-4 h-4 mr-2" />
            Limpiar
          </Button>
        )}
      </div>

      {/* Advanced filters */}
      {showAdvanced && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 pt-4 border-t">
          {/* Warehouse */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Almacén
            </label>
            <select
              value={warehouseId}
              onChange={(e) => setWarehouseId(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Todos los almacenes</option>
              {warehouses.map((warehouse) => (
                <option key={warehouse.id} value={warehouse.id}>
                  {warehouse.name}
                </option>
              ))}
            </select>
          </div>

          {/* Stock level */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Nivel de stock
            </label>
            <select
              value={lowStock === undefined ? '' : lowStock ? 'true' : 'false'}
              onChange={(e) =>
                setLowStock(
                  e.target.value === '' ? undefined : e.target.value === 'true'
                )
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Todos</option>
              <option value="true">Stock bajo</option>
              <option value="false">Stock normal</option>
            </select>
          </div>

          {/* Sort by */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Ordenar por
            </label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value as StockQueryParams['sortBy'])}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="product_name">Nombre del producto</option>
              <option value="quantity">Cantidad</option>
              <option value="last_movement_date">Último movimiento</option>
            </select>
          </div>

          {/* Sort order */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Orden
            </label>
            <select
              value={sortOrder}
              onChange={(e) => setSortOrder(e.target.value as 'ASC' | 'DESC')}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="ASC">Ascendente</option>
              <option value="DESC">Descendente</option>
            </select>
          </div>
        </div>
      )}

      {/* Apply button */}
      {showAdvanced && (
        <div className="flex justify-end pt-2 border-t">
          <Button onClick={handleApply}>Aplicar filtros</Button>
        </div>
      )}
    </div>
  );
}

'use client';

import { ArrowUpCircle, ArrowDownCircle, Edit3, TrendingUp, AlertCircle, RotateCcw } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import type { StockMovement } from '@/types/inventory';

interface StockMovementsProps {
  movements: StockMovement[];
  isLoading?: boolean;
}

export function StockMovements({ movements, isLoading }: StockMovementsProps) {
  const getMovementConfig = (type: StockMovement['movement_type']) => {
    const configs = {
      IN: {
        label: 'Entrada',
        icon: ArrowUpCircle,
        variant: 'success' as const,
        color: 'text-green-600',
      },
      OUT: {
        label: 'Salida',
        icon: ArrowDownCircle,
        variant: 'error' as const,
        color: 'text-red-600',
      },
      ADJUST: {
        label: 'Ajuste',
        icon: Edit3,
        variant: 'default' as const,
        color: 'text-blue-600',
      },
      TRANSFER: {
        label: 'Transferencia',
        icon: TrendingUp,
        variant: 'default' as const,
        color: 'text-purple-600',
      },
      DAMAGE: {
        label: 'Daño',
        icon: AlertCircle,
        variant: 'warning' as const,
        color: 'text-orange-600',
      },
      RETURN: {
        label: 'Devolución',
        icon: RotateCcw,
        variant: 'default' as const,
        color: 'text-indigo-600',
      },
    };
    return configs[type] || configs.ADJUST;
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('es-CO', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    }).format(date);
  };

  if (isLoading) {
    return (
      <div className="animate-pulse space-y-4">
        <div className="h-20 bg-gray-200 rounded"></div>
        <div className="h-20 bg-gray-200 rounded"></div>
        <div className="h-20 bg-gray-200 rounded"></div>
      </div>
    );
  }

  if (movements.length === 0) {
    return (
      <div className="text-center py-12">
        <TrendingUp className="w-16 h-16 mx-auto text-gray-300 mb-4" />
        <p className="text-gray-500 text-lg">No hay movimientos registrados</p>
        <p className="text-gray-400 text-sm mt-2">
          Los movimientos de stock aparecerán aquí
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-3">
      {movements.map((movement) => {
        const config = getMovementConfig(movement.movement_type);
        const Icon = config.icon;

        return (
          <div
            key={movement.id}
            className="flex items-center gap-4 p-4 bg-white border border-gray-200 rounded-lg hover:shadow-md transition-shadow"
          >
            {/* Icon */}
            <div className={`flex-shrink-0 w-12 h-12 rounded-full bg-gray-100 flex items-center justify-center ${config.color}`}>
              <Icon className="w-6 h-6" />
            </div>

            {/* Info */}
            <div className="flex-1 min-w-0">
              <div className="flex items-center gap-2 mb-1">
                <h4 className="font-medium text-gray-900">
                  {movement.product_name || 'Producto'}
                </h4>
                <Badge variant={config.variant}>{config.label}</Badge>
              </div>
              <div className="text-sm text-gray-600 space-y-1">
                <div className="flex items-center gap-4">
                  <span>
                    <span className="font-medium">Almacén:</span> {movement.warehouse_name}
                    {movement.location_name && ` - ${movement.location_name}`}
                  </span>
                  {movement.reference_number && (
                    <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded">
                      Ref: {movement.reference_number}
                    </span>
                  )}
                </div>
                {movement.notes && (
                  <p className="text-gray-500 italic">{movement.notes}</p>
                )}
              </div>
            </div>

            {/* Quantity and Date */}
            <div className="text-right">
              <div className={`text-2xl font-bold ${config.color}`}>
                {movement.movement_type === 'IN' || movement.movement_type === 'RETURN' ? '+' : '-'}
                {movement.quantity}
              </div>
              <div className="text-xs text-gray-500 mt-1">
                {formatDate(movement.created_at)}
              </div>
              {movement.user_name && (
                <div className="text-xs text-gray-400 mt-1">
                  Por: {movement.user_name}
                </div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { X, ArrowUpCircle, ArrowDownCircle, Edit3, AlertCircle, RotateCcw } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import type { StockLevel } from '@/types/inventory';

interface AdjustStockModalProps {
  isOpen: boolean;
  onClose: () => void;
  stock: StockLevel | null;
  onConfirm: (
    movementType: 'IN' | 'OUT' | 'ADJUST' | 'DAMAGE' | 'RETURN',
    quantity: number,
    referenceNumber?: string,
    notes?: string
  ) => void;
  isLoading?: boolean;
}

export function AdjustStockModal({
  isOpen,
  onClose,
  stock,
  onConfirm,
  isLoading,
}: AdjustStockModalProps) {
  const [movementType, setMovementType] = useState<'IN' | 'OUT' | 'ADJUST' | 'DAMAGE' | 'RETURN'>('IN');
  const [quantity, setQuantity] = useState<number>(0);
  const [referenceNumber, setReferenceNumber] = useState('');
  const [notes, setNotes] = useState('');

  if (!isOpen || !stock) return null;

  const handleConfirm = () => {
    if (quantity <= 0) {
      alert('Ingresa una cantidad válida');
      return;
    }

    if (movementType === 'OUT' && quantity > stock.available_quantity) {
      alert('No hay suficiente stock disponible');
      return;
    }

    onConfirm(
      movementType,
      quantity,
      referenceNumber || undefined,
      notes || undefined
    );

    // Reset form
    setQuantity(0);
    setReferenceNumber('');
    setNotes('');
  };

  const getNewQuantity = () => {
    switch (movementType) {
      case 'IN':
      case 'RETURN':
        return stock.quantity + quantity;
      case 'OUT':
      case 'DAMAGE':
        return stock.quantity - quantity;
      case 'ADJUST':
        return quantity;
      default:
        return stock.quantity;
    }
  };

  const movementTypes = [
    {
      id: 'IN' as const,
      label: 'Entrada',
      icon: ArrowUpCircle,
      color: 'green',
      description: 'Recibir stock',
    },
    {
      id: 'OUT' as const,
      label: 'Salida',
      icon: ArrowDownCircle,
      color: 'red',
      description: 'Retirar stock',
    },
    {
      id: 'ADJUST' as const,
      label: 'Ajuste',
      icon: Edit3,
      color: 'blue',
      description: 'Ajustar a cantidad exacta',
    },
    {
      id: 'DAMAGE' as const,
      label: 'Daño',
      icon: AlertCircle,
      color: 'orange',
      description: 'Registrar daño',
    },
    {
      id: 'RETURN' as const,
      label: 'Devolución',
      icon: RotateCcw,
      color: 'indigo',
      description: 'Devolución de cliente',
    },
  ];

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <h2 className="text-2xl font-bold text-gray-900">Ajustar Stock</h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
            disabled={isLoading}
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 space-y-6">
          {/* Product info */}
          <div className="bg-gray-50 rounded-lg p-4">
            <p className="text-sm text-gray-600 mb-1">Producto</p>
            <p className="text-lg font-semibold text-gray-900">{stock.product_name}</p>
            {stock.product_sku && (
              <p className="text-sm text-gray-500 font-mono">SKU: {stock.product_sku}</p>
            )}
            <div className="mt-3 grid grid-cols-3 gap-4 text-sm">
              <div>
                <span className="text-gray-600">Stock actual:</span>
                <span className="ml-2 font-bold text-gray-900">{stock.quantity}</span>
              </div>
              <div>
                <span className="text-gray-600">Reservado:</span>
                <span className="ml-2 font-bold text-gray-900">{stock.reserved_quantity}</span>
              </div>
              <div>
                <span className="text-gray-600">Disponible:</span>
                <span className="ml-2 font-bold text-green-600">{stock.available_quantity}</span>
              </div>
            </div>
          </div>

          {/* Movement type selection */}
          <div>
            <Label className="mb-3">Tipo de movimiento</Label>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
              {movementTypes.map((type) => {
                const Icon = type.icon;
                const isSelected = movementType === type.id;
                return (
                  <button
                    key={type.id}
                    onClick={() => setMovementType(type.id)}
                    className={`
                      p-3 rounded-lg border-2 transition-all text-left
                      ${
                        isSelected
                          ? `border-${type.color}-500 bg-${type.color}-50`
                          : 'border-gray-200 hover:border-gray-300'
                      }
                    `}
                  >
                    <Icon
                      className={`w-5 h-5 mb-2 ${
                        isSelected ? `text-${type.color}-600` : 'text-gray-400'
                      }`}
                    />
                    <p
                      className={`text-sm font-medium ${
                        isSelected ? `text-${type.color}-900` : 'text-gray-700'
                      }`}
                    >
                      {type.label}
                    </p>
                    <p className="text-xs text-gray-500 mt-1">
                      {type.description}
                    </p>
                  </button>
                );
              })}
            </div>
          </div>

          {/* Quantity input */}
          <div>
            <Label htmlFor="quantity">
              {movementType === 'ADJUST' ? 'Nueva cantidad' : 'Cantidad'}
            </Label>
            <Input
              id="quantity"
              type="number"
              min="0"
              value={quantity || ''}
              onChange={(e) => setQuantity(parseInt(e.target.value) || 0)}
              placeholder="0"
            />
            {movementType === 'OUT' && quantity > stock.available_quantity && (
              <p className="text-sm text-red-600 mt-1">
                Cantidad excede el stock disponible ({stock.available_quantity})
              </p>
            )}
          </div>

          {/* Reference number */}
          <div>
            <Label htmlFor="reference">Número de referencia (Opcional)</Label>
            <Input
              id="reference"
              type="text"
              value={referenceNumber}
              onChange={(e) => setReferenceNumber(e.target.value)}
              placeholder="Ej: PO-12345, FACTURA-001"
            />
          </div>

          {/* Notes */}
          <div>
            <Label htmlFor="notes">Notas (Opcional)</Label>
            <textarea
              id="notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Motivo del ajuste, observaciones..."
              rows={3}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Preview */}
          {quantity > 0 && (
            <div className="bg-blue-50 rounded-lg p-4 border border-blue-200">
              <p className="text-sm text-blue-700 mb-2">Vista previa</p>
              <div className="flex items-center justify-between">
                <span className="text-sm text-blue-600">Stock actual:</span>
                <span className="font-semibold text-blue-900">{stock.quantity}</span>
              </div>
              {movementType !== 'ADJUST' && (
                <div className="flex items-center justify-between mt-1">
                  <span className="text-sm text-blue-600">
                    {['IN', 'RETURN'].includes(movementType) ? 'Agregar:' : 'Restar:'}
                  </span>
                  <span
                    className={`font-semibold ${
                      ['IN', 'RETURN'].includes(movementType)
                        ? 'text-green-600'
                        : 'text-red-600'
                    }`}
                  >
                    {['IN', 'RETURN'].includes(movementType) ? '+' : '-'}
                    {quantity}
                  </span>
                </div>
              )}
              <div className="flex items-center justify-between mt-2 pt-2 border-t border-blue-300">
                <span className="text-sm font-semibold text-blue-700">Nuevo stock:</span>
                <span className="text-xl font-bold text-blue-900">{getNewQuantity()}</span>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex gap-3 p-6 border-t bg-gray-50">
          <Button
            variant="outline"
            onClick={onClose}
            disabled={isLoading}
            className="flex-1"
          >
            Cancelar
          </Button>
          <Button
            onClick={handleConfirm}
            disabled={isLoading || quantity <= 0}
            className="flex-1"
          >
            {isLoading ? 'Procesando...' : 'Confirmar Ajuste'}
          </Button>
        </div>
      </div>
    </div>
  );
}

