import { Badge } from '@/components/ui/badge';
import { useProducts } from '@/hooks/useProducts';

interface Customer {
  id: string;
  name: string;
  email?: string;
  phone?: string;
  loyalty_points?: number;
}

interface CustomerSelectProps {
  selectedCustomer?: Customer;
  onSelect: (customer: Customer | undefined) => void;
}

export function CustomerSelect({
  selectedCustomer,
  onSelect,
}: CustomerSelectProps) {
  const [showSearch, setShowSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  // Mock customers - In real app, use useCustomers hook
  const mockCustomers: Customer[] = [
    {
      id: '1',
      name: 'Cliente General',
      email: 'general@example.com',
      loyalty_points: 0,
    },
    {
      id: '2',
      name: 'Juan Pérez',
      email: 'juan@example.com',
      phone: '+57 300 123 4567',
      loyalty_points: 150,
    },
    {
      id: '3',
      name: 'María García',
      email: 'maria@example.com',
      phone: '+57 301 987 6543',
      loyalty_points: 320,
    },
  ];

  const filteredCustomers = mockCustomers.filter(
    (c) =>
      c.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      c.email?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      c.phone?.includes(searchQuery)
  );

  const handleSelect = (customer: Customer) => {
    onSelect(customer);
    setShowSearch(false);
    setSearchQuery('');
  };

  const handleClear = () => {
    onSelect(undefined);
  };

  return (
    <div className="bg-white rounded-lg border border-gray-200 p-4">
      <div className="flex items-center justify-between mb-3">
        <label className="text-sm font-medium text-gray-700">Cliente (Opcional)</label>
        {selectedCustomer && (
          <Button
            variant="ghost"
            size="sm"
            onClick={handleClear}
            className="text-gray-500"
          >
            <X className="w-4 h-4" />
          </Button>
        )}
      </div>

      {selectedCustomer ? (
        <div className="flex items-center gap-3 p-3 bg-blue-50 rounded-lg">
          <div className="w-10 h-10 rounded-full bg-blue-200 flex items-center justify-center">
            <User className="w-5 h-5 text-blue-700" />
          </div>
          <div className="flex-1 min-w-0">
            <p className="font-medium text-gray-900">{selectedCustomer.name}</p>
            {selectedCustomer.email && (
              <p className="text-sm text-gray-500">{selectedCustomer.email}</p>
            )}
          </div>
          {selectedCustomer.loyalty_points !== undefined &&
            selectedCustomer.loyalty_points > 0 && (
              <Badge variant="success">
                {selectedCustomer.loyalty_points} puntos
              </Badge>
            )}
        </div>
      ) : (
        <div className="relative">
          <Button
            variant="outline"
            onClick={() => setShowSearch(!showSearch)}
            className="w-full justify-start"
          >
            <Search className="w-4 h-4 mr-2" />
            Buscar cliente...
          </Button>

          {showSearch && (
            <>
              <div className="absolute z-10 w-full mt-2 bg-white border border-gray-200 rounded-lg shadow-lg max-h-80 overflow-hidden">
                {/* Search input */}
                <div className="p-3 border-b">
                  <Input
                    type="text"
                    placeholder="Buscar por nombre, email o teléfono..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    autoFocus
                  />
                </div>

                {/* Results */}
                <div className="max-h-64 overflow-y-auto">
                  {filteredCustomers.length === 0 ? (
                    <div className="p-8 text-center text-gray-500">
                      <User className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                      <p>No se encontraron clientes</p>
                    </div>
                  ) : (
                    <div className="divide-y divide-gray-100">
                      {filteredCustomers.map((customer) => (
                        <div
                          key={customer.id}
                          className="p-3 hover:bg-gray-50 cursor-pointer transition-colors"
                          onClick={() => handleSelect(customer)}
                        >
                          <div className="flex items-center gap-3">
                            <div className="w-10 h-10 rounded-full bg-gray-200 flex items-center justify-center">
                              <User className="w-5 h-5 text-gray-600" />
                            </div>
                            <div className="flex-1 min-w-0">
                              <p className="font-medium text-gray-900">
                                {customer.name}
                              </p>
                              <div className="flex items-center gap-2 text-sm text-gray-500">
                                {customer.email && <span>{customer.email}</span>}
                                {customer.phone && (
                                  <>
                                    {customer.email && <span>•</span>}
                                    <span>{customer.phone}</span>
                                  </>
                                )}
                              </div>
                            </div>
                            {customer.loyalty_points !== undefined &&
                              customer.loyalty_points > 0 && (
                                <Badge variant="success" className="text-xs">
                                  {customer.loyalty_points}
                                </Badge>
                              )}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              <div
                className="fixed inset-0 z-0"
                onClick={() => setShowSearch(false)}
              />
            </>
          )}
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Search, Plus, Package } from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { useSearchProducts } from '@/hooks/useProducts';
import type { Product } from '@/types/product';

interface ProductSearchProps {
  onAddProduct: (product: Product) => void;
}

export function ProductSearch({ onAddProduct }: ProductSearchProps) {
  const [query, setQuery] = useState('');
  const [showResults, setShowResults] = useState(false);
  const { data: results, isLoading } = useSearchProducts(query);

  useEffect(() => {
    setShowResults(query.length > 0);
  }, [query]);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const handleAddProduct = (product: Product) => {
    onAddProduct(product);
    setQuery('');
    setShowResults(false);
  };

  const getStockBadge = (product: Product) => {
    if (product.stock_quantity === 0) {
      return <Badge variant="error">Sin stock</Badge>;
    }
    if (product.min_stock_level && product.stock_quantity <= product.min_stock_level) {
      return <Badge variant="warning">Stock bajo</Badge>;
    }
    return <Badge variant="success">Disponible</Badge>;
  };

  return (
    <div className="relative">
      {/* Search input */}
      <div className="relative">
        <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
        <Input
          type="text"
          placeholder="Buscar por nombre, SKU o código de barras..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="pl-10"
        />
      </div>

      {/* Results dropdown */}
      {showResults && (
        <div className="absolute z-10 w-full mt-2 bg-white border border-gray-200 rounded-lg shadow-lg max-h-96 overflow-y-auto">
          {isLoading && (
            <div className="p-4 text-center text-gray-500">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
              <p className="mt-2 text-sm">Buscando productos...</p>
            </div>
          )}

          {!isLoading && results && results.length === 0 && (
            <div className="p-8 text-center">
              <Package className="w-12 h-12 mx-auto text-gray-300 mb-2" />
              <p className="text-gray-500">No se encontraron productos</p>
              <p className="text-sm text-gray-400 mt-1">
                Intenta con otro término de búsqueda
              </p>
            </div>
          )}

          {!isLoading && results && results.length > 0 && (
            <div className="divide-y divide-gray-100">
              {results.map((product) => (
                <div
                  key={product.id}
                  className="p-4 hover:bg-gray-50 transition-colors cursor-pointer"
                  onClick={() => handleAddProduct(product)}
                >
                  <div className="flex items-center gap-3">
                    {/* Product image */}
                    {product.image_url ? (
                      <img
                        src={product.image_url}
                        alt={product.name}
                        className="w-12 h-12 rounded object-cover"
                      />
                    ) : (
                      <div className="w-12 h-12 rounded bg-gray-200 flex items-center justify-center">
                        <Package className="w-6 h-6 text-gray-400" />
                      </div>
                    )}

                    {/* Product info */}
                    <div className="flex-1 min-w-0">
                      <h4 className="font-medium text-gray-900 truncate">
                        {product.name}
                      </h4>
                      <div className="flex items-center gap-2 mt-1">
                        <span className="text-sm text-gray-500 font-mono">
                          SKU: {product.sku}
                        </span>
                        {getStockBadge(product)}
                      </div>
                    </div>

                    {/* Price and action */}
                    <div className="text-right">
                      <p className="text-lg font-bold text-gray-900">
                        {formatCurrency(product.price)}
                      </p>
                      <p className="text-sm text-gray-500">
                        Stock: {product.stock_quantity}
                      </p>
                    </div>

                    <Button
                      variant="outline"
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleAddProduct(product);
                      }}
                      disabled={product.stock_quantity === 0}
                    >
                      <Plus className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Backdrop */}
      {showResults && (
        <div
          className="fixed inset-0 z-0"
          onClick={() => setShowResults(false)}
        />
      )}
    </div>
  );
}

'use client';

import { Trash2, Plus, Minus } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import type { CartItem } from '@/types/sale';

interface SalesCartProps {
  items: CartItem[];
  onUpdateQuantity: (productId: string, quantity: number) => void;
  onRemoveItem: (productId: string) => void;
  subtotal: number;
  tax: number;
  discount: number;
  total: number;
}

export function SalesCart({
  items,
  onUpdateQuantity,
  onRemoveItem,
  subtotal,
  tax,
  discount,
  total,
}: SalesCartProps) {
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  if (items.length === 0) {
    return (
      <Card className="p-8 text-center">
        <div className="text-gray-400 mb-2">
          <svg
            className="w-16 h-16 mx-auto mb-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z"
            />
          </svg>
        </div>
        <p className="text-lg font-medium text-gray-600">Carrito vacío</p>
        <p className="text-sm text-gray-400 mt-1">
          Busca productos para agregar al carrito
        </p>
      </Card>
    );
  }

  return (
    <Card className="flex flex-col h-full">
      {/* Items list */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {items.map((item) => (
          <div
            key={item.product_id}
            className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg"
          >
            {/* Product image */}
            {item.image_url ? (
              <img
                src={item.image_url}
                alt={item.product_name}
                className="w-12 h-12 rounded object-cover"
              />
            ) : (
              <div className="w-12 h-12 rounded bg-gray-200 flex items-center justify-center">
                <span className="text-gray-400 text-xs">IMG</span>
              </div>
            )}

            {/* Product info */}
            <div className="flex-1 min-w-0">
              <h4 className="font-medium text-gray-900 truncate">
                {item.product_name}
              </h4>
              <p className="text-sm text-gray-500">
                {formatCurrency(item.unit_price)} x {item.quantity}
              </p>
            </div>

            {/* Quantity controls */}
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => onUpdateQuantity(item.product_id, item.quantity - 1)}
                disabled={item.quantity <= 1}
                className="w-8 h-8 p-0"
              >
                <Minus className="w-4 h-4" />
              </Button>
              <span className="w-8 text-center font-medium">{item.quantity}</span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => onUpdateQuantity(item.product_id, item.quantity + 1)}
                disabled={item.quantity >= item.stock_quantity}
                className="w-8 h-8 p-0"
              >
                <Plus className="w-4 h-4" />
              </Button>
            </div>

            {/* Subtotal and remove */}
            <div className="text-right">
              <p className="font-semibold text-gray-900">
                {formatCurrency(item.subtotal)}
              </p>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onRemoveItem(item.product_id)}
                className="text-red-600 hover:text-red-700 hover:bg-red-50 mt-1"
              >
                <Trash2 className="w-4 h-4" />
              </Button>
            </div>
          </div>
        ))}
      </div>

      {/* Summary */}
      <div className="border-t border-gray-200 p-4 space-y-2">
        <div className="flex justify-between text-sm text-gray-600">
          <span>Subtotal:</span>
          <span>{formatCurrency(subtotal)}</span>
        </div>
        {discount > 0 && (
          <div className="flex justify-between text-sm text-green-600">
            <span>Descuento:</span>
            <span>-{formatCurrency(discount)}</span>
          </div>
        )}
        <div className="flex justify-between text-sm text-gray-600">
          <span>IVA:</span>
          <span>{formatCurrency(tax)}</span>
        </div>
        <div className="flex justify-between text-lg font-bold text-gray-900 pt-2 border-t">
          <span>Total:</span>
          <span>{formatCurrency(total)}</span>
        </div>
      </div>
    </Card>
  );
}

'use client';

import { useState } from 'react';
import { X, CreditCard, DollarSign, Smartphone } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';

interface PaymentModalProps {
  isOpen: boolean;
  onClose: () => void;
  total: number;
  onConfirm: (paymentMethod: string, notes?: string) => void;
  isLoading?: boolean;
}

export function PaymentModal({
  isOpen,
  onClose,
  total,
  onConfirm,
  isLoading,
}: PaymentModalProps) {
  const [paymentMethod, setPaymentMethod] = useState<string>('cash');
  const [notes, setNotes] = useState('');
  const [cashReceived, setCashReceived] = useState<number>(total);

  if (!isOpen) return null;

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-CO', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const change = cashReceived - total;

  const paymentMethods = [
    {
      id: 'cash',
      label: 'Efectivo',
      icon: DollarSign,
      color: 'green',
    },
    {
      id: 'card',
      label: 'Tarjeta',
      icon: CreditCard,
      color: 'blue',
    },
    {
      id: 'transfer',
      label: 'Transferencia',
      icon: Smartphone,
      color: 'purple',
    },
  ];

  const handleConfirm = () => {
    if (paymentMethod === 'cash' && cashReceived < total) {
      alert('El monto recibido debe ser mayor o igual al total');
      return;
    }
    onConfirm(paymentMethod, notes || undefined);
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-md max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <h2 className="text-2xl font-bold text-gray-900">Finalizar Venta</h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
            disabled={isLoading}
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 space-y-6">
          {/* Total */}
          <div className="bg-gray-50 rounded-lg p-4">
            <p className="text-sm text-gray-600 mb-1">Total a pagar</p>
            <p className="text-3xl font-bold text-gray-900">
              {formatCurrency(total)}
            </p>
          </div>

          {/* Payment method selection */}
          <div>
            <Label className="mb-3">Método de pago</Label>
            <div className="grid grid-cols-3 gap-3">
              {paymentMethods.map((method) => {
                const Icon = method.icon;
                const isSelected = paymentMethod === method.id;
                return (
                  <button
                    key={method.id}
                    onClick={() => setPaymentMethod(method.id)}
                    className={`
                      p-4 rounded-lg border-2 transition-all
                      ${
                        isSelected
                          ? `border-${method.color}-500 bg-${method.color}-50`
                          : 'border-gray-200 hover:border-gray-300'
                      }
                    `}
                  >
                    <Icon
                      className={`w-8 h-8 mx-auto mb-2 ${
                        isSelected ? `text-${method.color}-600` : 'text-gray-400'
                      }`}
                    />
                    <p
                      className={`text-sm font-medium ${
                        isSelected ? `text-${method.color}-900` : 'text-gray-700'
                      }`}
                    >
                      {method.label}
                    </p>
                  </button>
                );
              })}
            </div>
          </div>

          {/* Cash specific fields */}
          {paymentMethod === 'cash' && (
            <div className="space-y-4">
              <div>
                <Label htmlFor="cashReceived">Efectivo recibido</Label>
                <Input
                  id="cashReceived"
                  type="number"
                  step="1000"
                  min={total}
                  value={cashReceived}
                  onChange={(e) => setCashReceived(parseFloat(e.target.value) || 0)}
                  placeholder="0"
                />
              </div>

              {cashReceived >= total && (
                <div className="bg-green-50 rounded-lg p-4">
                  <p className="text-sm text-green-700 mb-1">Cambio</p>
                  <p className="text-2xl font-bold text-green-900">
                    {formatCurrency(change)}
                  </p>
                </div>
              )}
            </div>
          )}

          {/* Notes */}
          <div>
            <Label htmlFor="notes">Notas (Opcional)</Label>
            <textarea
              id="notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Ej: Cliente pidió factura, entregar en domicilio..."
              rows={3}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
        </div>

        {/* Footer */}
        <div className="flex gap-3 p-6 border-t bg-gray-50">
          <Button
            variant="outline"
            onClick={onClose}
            disabled={isLoading}
            className="flex-1"
          >
            Cancelar
          </Button>
          <Button
            onClick={handleConfirm}
            disabled={isLoading || (paymentMethod === 'cash' && cashReceived < total)}
            className="flex-1"
          >
            {isLoading ? 'Procesando...' : 'Confirmar Venta'}
          </Button>
        </div>
      </div>
    </div>
  );
}

